var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();
var saveQuery ="F";

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	setClass(ComboBoxClass,ds_Class); //분류지정(1:예비품, 2:비품, 3:식자재)
	getComCode(); //공장구분 선택
	ed_ordid.Text = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7); //발주담당자 사번
	st_ordid.Text = G_ds_user.GetColumn(0,"USER_NAME");          //발주담당자 명
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;
	fn_Search();
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getogm106(){
	var group = ComboBoxClass.Value;
	var sorderno = trim(ed_ordno.Text);//발주번호
	var sordid = trim(ed_ordid.Text);  //발주담당자
	var sSts; // 발주승인
	if(rd_gubun.Value == "A"){ 
		sSts = "10";
	}else if(rd_gubun.Value == "B"){ 
		sSts = "20";
	}
	var sKind		= "getOgm106";						
	var sMethodName = "service::psogmlib/selectOGM106.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm106=ds_ogm106";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@GROUP=" + quote(group); //그룹
	sArgument += "@ORSTS=" + quote(sSts); //발주상태
	sArgument += "@ORDNO=" + quote(sorderno); //발주번호
	//sArgument += "@ORDID=" + quote(sordid);   //발주담당자
	sArgument += "@DPTCD=" + quote(trim(ed_dptcd.Value));      //관리부서코드
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (saveQuery == "F"){
		if (str == 'T'){  //정상조회
			alert(setPopMessage("1004"));
			setMessage("1004");	
		}else if (str == 'B'){   //data값이 없을때
			alert(setPopMessage("1003"));
			setMessage("1003");	
		}else if (str == 'E'){   //error발생시
			alert(setPopMessage("1002"));
			setMessage("1002");	
		}
	}
}

/********************************************************************** 
 * 내용설명 : LIST발행
 **********************************************************************/ 
function imgbtnPrint_OnClick(obj,nX,nY)
{

	if(ds_ogm106.rowcount() < 1) {
		alert(setPopMessage("2509"));  //[알림] 조회후 실행하세요!
		setMessage("2509");	
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm106.rowcount(); i++){
		if (ds_ogm106.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));  //[알림] 선택한 품목이 없습니다.!
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목을 발주확정 하시겠습니까? ") == false) return;

	for(var i=0; i<ds_ogm106.rowcount(); i++){
		if (ds_ogm106.GetColumn(i, "SEL") == "1" ){
			if(length(trim(ds_ogm106.GetColumn(i, "ORDNO"))) != 0){

				http.Sync 	= true;
				var sKind		= "updateOGM106_PR";						
				var sMethodName = "service::psogmlib/updateOGM106_HOLD_PR.do";  // 발주취소와 동시사용 서버에서 호출 될 Method 명
				var sInDataSet  = "ds_ogm106=ds_ogm106:U";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				//target
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);  
				sArgument += " ORDNO=" + quote(ds_ogm106.GetColumn(i, "ORDNO")); 
				sArgument += " PRTNO=" + quote(ds_OGM106.GetColumn(i,"PRTNO"));//prtno
				//target value
				sArgument += " ORQTY=" + quote(ds_OGM106.GetColumn(i,"ORQTY"));
				sArgument += " ORDAT=" + quote(Calendar0.Value);  //2012/04/18 추가(pgm오류)
				//sArgument += " JANQY=" + quote(ds_OGM106.GetColumn(i,"ORQTY"));  2012/04/18 삭제(pgm오류)
				sArgument += " ORSTS=" + quote("20");   //발주상태 20=발주
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}
		}
	}
	saveQuery="T";
	getOgm106();  //처리후 재조회

	//발행~
	reportQuery();
	dialog("psogmlib::ogm106rep.xml","");
	alert("인쇄가 완료되면 인쇄승인을 클릭하여 발주승인처리 해주세요");

}

function reportQuery()
{
	http.Sync 	= true;
	var sKind		= "getogm106_rep";						
	var sMethodName = "service::psogmlib/selectOGM106_rep.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm106rep=ds_ogm106rep";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GROUP=" + quote(ComboBoxClass.Value);      
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 내용설명 : 인쇄승인
 **********************************************************************/ 
function imgbtnOK_OnClick(obj,nX,nY)
{
	if(confirm("승인처리 하시겠습니까?              ") == false) return;
	var sKind		= "updateOGM106_OK";						
	var sMethodName = "service::psogmlib/updateOGM106_OK.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm106=ds_ogm106:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GROUP=" + quote(ComboBoxClass.Value);       //코드분류 
	sArgument += " ORSTS=" + quote("20");  //발주상태 20=발주
	//target value
	sArgument += " PT1YN=" + quote("PT1YN");
	sArgument += " PT2YN=" + quote("Y");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");	
}

/********************************************************************** 
 * 내용설명 : 발주취소
 **********************************************************************/ 
function imgbtnHold_OnClick(obj,nX,nY)
{
	if(ds_ogm106.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");	
		return;
	}
	if(rd_gubun.Value != "A"){
		alert("발주승인 품목을 조회한 후 취소하세요  ");
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm106.rowcount(); i++){
		if (ds_ogm106.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목을 발주취소 하시겠습니까? ") == false) return;

	for(var i=0; i<ds_ogm106.rowcount(); i++){
		if (ds_ogm106.GetColumn(i, "SEL") == "1" ){
			if(length(trim(ds_ogm106.GetColumn(i, "ORDNO"))) != 0){
				//UPDATE
				http.Sync 	= true;
				var sKind		= "updateOGM106_HOLD";						
				var sMethodName = "service::psogmlib/updateOGM106_HOLD_CANCEL.do";  
				var sInDataSet  = "ds_ogm106=ds_ogm106:U";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				//target
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);  
				sArgument += " ORDNO=" + quote(ds_ogm106.GetColumn(i, "ORDNO")); 
				sArgument += " PRTNO=" + quote(ds_OGM106.GetColumn(i,"PRTNO"));//prtno
				//target value
				sArgument += " ORQTY=" + quote(ds_OGM106.GetColumn(i,"ORQTY"));
				sArgument += " JANQY=" + quote(ds_OGM106.GetColumn(i,"ORQTY"));
				sArgument += " ORSTS=" + quote("90");   //발주상태 90=발주취소
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}
		}
	}
	saveQuery="T";
	getOgm106();  //처리후 재조회
	alert(setPopMessage("1001"));
	setMessage("1001");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="updateOGM106_OK"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : 구매번호입력시 Checkbox에 Check상태로 변경
 **********************************************************************/ 
function ds_ogm106_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "ORQTY" ){
		ds_ogm106.SetColumn(nRow,"SEL","1");
	}
}

/********************************************************************** 
 * 내용설명 : 부서명 조회
 **********************************************************************/ 
function imgDptcd_OnClick(obj,nX,nY)
{
	var sDptcd=trim(ed_dptcd.Text);
	if (length(trim(ed_dptcd.Text)) == 0) sDptcd="zzzzzzzzzz";
	var selectnumber = "6";    // 1.코드 2.품목 3.업체 4.Maker 5.사용자 , 6.부서  7.설비 8.사원
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sDptcd,584,528);
	if (length(rtn) != 0){
		ed_dptcd.Text = rtn[0];
		st_dptcdnm.Text = rtn[1];
	}
}

/********************************************************************** 
 * 내용설명 : 부서명 조회
 **********************************************************************/ 
function ed_dptcd_OnCharChanged(obj,strPreText,strPostText)
{
	st_dptcdnm.Text = "";
	if(length(trim(ed_dptcd.Value)) == 4){
		var sKind		= "getogm106_dchk";						
		var sMethodName = "service::pscomlib/selectMST650.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_mst650PF=ds_mst650PF";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " DPCODE=" + quote(trim(ed_dptcd.Value));       //부서코드
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "발주등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|발주등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_ogm106.DeleteAll();
	ds_ogm106.ClearData();
	//ds_ogm106_dchk.DeleteAll();

}
function rd_gubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_ogm106.DeleteAll();
	ds_ogm106.ClearData();
	getOgm106();
	if(strCode == "A"){
		imgbtnHold.Visible = true;
		imgbtnPrint.Visible = true;
		imgbtnOK.Visible = true;
	}else if(strCode == "B"){
		imgbtnHold.Visible = false;
		imgbtnPrint.Visible = false;
		imgbtnOK.Visible = false;
	}

}
