/********************************************************************** 
 * 내용설명 : 사용자 조회 (예비품관리사용자)
 **********************************************************************/ 
function imgIndid_OnClick(obj,nX,nY)
{
	var sIndid=trim(ed_ordid.Text);
	if (length(trim(ed_ordid.Text)) == 0) sIndid="zzzzzzzzzz";
	var sMcode="10073"; //10073=예비품    10083=설비보전
	var selectnumber = "5";    // 1.코드 2.품목 3.업체 4.Maker 5.사용자 , 6.부서  7.설비 8.사원
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sIndid + " " + "MCODE=" + sMcode,584,528);
	if (length(rtn) != 0){
		ed_ordid.Text = substr(rtn[0],1,7);
		st_ordid.Text = rtn[1];
	}
}

function ds_ogm106_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "ORQTY"){
		if (ToNumber(varNewValue) < 1) {
			alert(setPopMessage("2517"));
			setMessage("2517");	
			return false;
		}
	}
}
