var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();
var saveQuery ="F";

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	setClass(ComboBoxClass,ds_Class); //분류지정(1:예비품, 2:비품, 3:식자재)
	getComCode(); //공장구분 선택
	gFcOnTopButtonColor("2.3"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	ca_Indat_from.Value = substr(nowDate,0,6)+"01";
	MainGrid.UserData = MainGrid.Height;
	fn_Search();
	//ed_indid.Text = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7); //발주담당자 사번
	//st_indidName.Text = G_ds_user.GetColumn(0,"USER_NAME");          //발주담당자 명
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getogm126(){
	var group = ComboBoxClass.Value;
	var sINPno = trim(ed_inpno.Text); //입고번호
	//var sVndor = trim(txtvndor.Text);    //구입선
	var sIndid = trim(ed_indid.Text);    //입고담당자
	var sDptcd = trim(ed_dptcd.Text);    //부서
	var sIndatfrom = ca_Indat_from.Value;    //from 일자
	var sIndatto = ca_Indat_to.Value;        //to 일자

	var sKind		= "getOgm126";						
	var sMethodName = "service::psogmlib/selectOGM126.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OGM126=ds_OGM126";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@LIB3=" + quote(Lib3);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@GROUP=" + quote(group); //그룹
	sArgument += "@OUTNO=" + quote(sINPno); //출고번호
	sArgument += "@USEID=" + quote(sIndid); //담당자
	sArgument += "@OUDATFROM=" + quote(sIndatfrom); //
	sArgument += "@OUDATTO=" + quote(sIndatto); //
	sArgument += "@DPTCD=" + quote(sDptcd); // 부서
	//sArgument += "@VNDOR=" + quote(sVndor); //구입선
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (saveQuery == "F"){
		if (str == 'T'){  //정상조회
			alert(setPopMessage("1004"));
			setMessage("1004");	
		}else if (str == 'B'){   //data값이 없을때
			alert(setPopMessage("1003"));
			setMessage("1003");	
		}else if (str == 'E'){   //error발생시
			alert(setPopMessage("1002"));
			setMessage("1002");	
		}
	}
}

/********************************************************************** 
 * 내용설명 : 저장 update
 **********************************************************************/ 
function setogm126(){
	if (!closeYN(Lib2,ComboBoxPlant.Value,ComboBoxClass.Value,substr(ca_Oudat.Value,0,6))){
		alert(setPopMessage("2500"));
		setMessage("2500");	
		return;
	}

	if(ds_ogm126.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");	
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm126.rowcount(); i++){
		if (ds_ogm126.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목의 출고를 취소 하시겠습니까? ") == false) return;

	var iNo = 0;
	for(var i=0; i<ds_ogm126.rowcount(); i++){
		if (ds_ogm126.GetColumn(i, "SEL") == "1" ){
			if(ToNumber(ds_ogm126.GetColumn(i, "CQTY")) > 0){
				//------------------INSERT
				if (iNo == 0){
					maxNumberSelect();//출고번호구하기
					var max_no = ds_OGM125_maxNumber.GetColumn(0,"MAXOUTNO");
					iNo = ToNumber(max_no);
				}else{
					iNo = iNo+1;
				}
				var inpnumber;
				if (iNo > 99999999) {
					inpnumber = ""+iNo;
				}else if (iNo > 9999999) {
					inpnumber = "0"+iNo;
				}else if (iNo > 999999) {
					inpnumber = "00"+iNo;
				}else if (iNo > 99999) {
					inpnumber = "000"+iNo;
				}else if (iNo > 9999) {
					inpnumber = "0000"+iNo;
				}else if (iNo > 999) {
					inpnumber = "00000"+iNo;
				}else if (iNo > 99) {
					inpnumber = "000000"+iNo;
				}else if (iNo > 9) {
					inpnumber = "0000000"+iNo;
				}else if (iNo > 0) {
					inpnumber = "00000000"+iNo;
				}
				var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
				var sOUQTY = toNumber(ds_ogm126.GetColumn(i, "CQTY")) * (-1);
				var sOCOST = toNumber(ds_ogm126.GetColumn(i, "OCOST"));
				http.Sync 	= true;
				var sKind		= "insertOGM126";						
				var sMethodName = "service::psogmlib/insertOGM125.do";     // 서버에서 호출 될 Method 명
				var sInDataSet  = "";                                     //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);  
				sArgument += " OUTNO=" + quote(inpnumber); 
				sArgument += " PRTNO=" + quote(ds_ogm126.GetColumn(i, "PRTNO"));//prtno
				sArgument += " PUNIT=" + quote(ds_ogm126.GetColumn(i, "PUNIT"));//punit
				sArgument += " OUDAT=" + quote(ca_Oudat.Value);
				sArgument += " OUTYM=" + quote(parseInt(toNumber(ca_Oudat.Value)/100));
				sArgument += " OUTID=" + quote("0");
				sArgument += " OUGUB=" + quote("90");  //10:정상, 20:강제, 90:취소
				sArgument += " OUQTY=" + quote(sOUQTY);
				sArgument += " OCOST=" + quote(sOCOST);
				sArgument += " OUUSE=" + quote("");
				sArgument += " DPTCD=" + quote(ds_ogm126.GetColumn(i, "DPTCD"));
				sArgument += " MSHNO=" + quote(ds_ogm126.GetColumn(i, "MSHNO"));
				sArgument += " USEID=" + quote(ds_ogm126.GetColumn(i, "USEID"));
				sArgument += " JISNO=" + quote("");
				sArgument += " OCNNO=" + quote(ds_ogm126.GetColumn(i, "OUTNO"));
				//addat,adtim
				sArgument += " ADUSR=" + quote(idnum);  //등록자
				//chdat,chtim
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}
		}
	}
	saveQuery="T";
	getOgm126();  //처리후 재조회
}

/********************************************************************** 
 * 내용설명 : 자재출고번호 구하기
 **********************************************************************/ 
function maxNumberSelect()
{
	http.Sync 	= true;
	var sKind		= "getogm126_maxNumber";						
	var sMethodName = "service::psogmlib/selectOGM125_maxNumber.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_OGM125_maxNumber=ds_OGM125_maxNumber";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GROUP=" + quote(ComboBoxClass.Value);      
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="insertOGM126"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : 부서명 조회
 **********************************************************************/ 
function imgDptcd_OnClick(obj,nX,nY)
{
	var sDptcd=trim(ed_dptcd.Text);
	if (length(trim(ed_dptcd.Text)) == 0) sDptcd="zzzzzzzzzz";
	var selectnumber = "6";    // 1.코드 2.품목 3.업체 4.Maker 5.사용자 , 6.부서  7.설비 8.사원
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sDptcd,584,528);
	if (length(rtn) != 0){
		ed_dptcd.Text = rtn[0];
		txtdptcdnm.Text = rtn[1];
	}
}

/********************************************************************** 
 * 내용설명 : 사용자 조회 (인사급여미스타 사용자)
