}

/********************************************************************** 
 * 내용설명 : 수량입력시 Checkbox에 Check상태로 변경
 **********************************************************************/ 
function ds_ogm126_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "CQTY" ){
		if (varNewValue != "0") {
			ds_ogm126.SetColumn(nRow,"SEL","1");
		}else{
			ds_ogm126.SetColumn(nRow,"SEL","0");
		}
	}
}


function ds_OGM126_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "CQTY"){
		if (ToNumber(varNewValue) > ToNumber(ds_ogm126.GetColumn(nRow,"OUQTY")) + ToNumber(ds_ogm126.GetColumn(nRow,"DOUQTY")) ) {
			alert(setPopMessage("2527"));
			setMessage("2527");	
			return false;
		}
		if (ToNumber(varNewValue) < 0) {
			alert(setPopMessage("2517"));
			setMessage("2517");	
			return false;
		}
	}
}


