function txtprtno_OnCharChanged(obj,strPreText,strPostText)
{
	txtprtnm.Text = "";
}

function txtprtno_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if( nChar == 13 && length(trim(txtprtno.Text)) != 0 ){ //enter
		http.Sync 	= true;
		var sKind		= "getogm010_dchk";						
		var sMethodName = "service::psogmlib/selectOGM010_dchk.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_OGM010_dchk=ds_OGM010_dchk";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(ComboBoxClass.Value); //그룹
		sArgument += " PRTNO=" + quote(trim(txtprtno.Text)); //신규품번		
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_OGM010_dchk.GetRowCount() == 0){
			alert(setPopMessage("2508"));
			setMessage("2508");
		}
	}
}

function imgPrtno_OnClick(obj,nX,nY)
{	
	var sPrtno=trim(txtprtno.Text);
	if (length(trim(txtprtno.Text)) == 0) sPrtno="zzzzzzzzzz";
	var selectnumber = "2";
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sPrtno,584,528);
	if (length(rtn) != 0){
		txtprtno.Text = rtn[0];
		txtprtnm.Text = rtn[1];
	}
}



/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_ogm137.DeleteAll();
	ds_ogm137.ClearData();
}


function imgMstcd_OnClick(obj,nX,nY)
{
	var sMstcd;
	if (obj.ID == "imgMstcdF"){
		sMstcd=trim(ed_MstcdF.Text);
		if (length(trim(ed_MstcdF.Text)) == 0) sMstcd="zzzzzzzzzz";
	}else if (obj.ID == "imgMstcdT"){
		sMstcd=trim(ed_MstcdT.Text);
		if (length(trim(ed_MstcdT.Text)) == 0) sMstcd="zzzzzzzzzz";
	}else{
		return;
	}
	var selectnumber = "9";
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sMstcd,584,528);
	if (length(rtn) != 0){
		if (obj.ID == "imgMstcdF"){
			ed_MstcdF.Text = rtn[0];
			st_MstcdF.Text = rtn[1];
		}else if (obj.ID == "imgMstcdT"){
			ed_MstcdT.Text = rtn[0];
			st_MstcdT.Text = rtn[1];
		}
	}
}

function ed_MstcdF_OnCharChanged(obj,strPreText,strPostText)
{
	st_MstcdF.Text = "";
}

function ed_MstcdT_OnCharChanged(obj,strPreText,strPostText)
{
	st_MstcdT.Text = "";
}
