/********************************************************************** 
 * 내용설명 : 품질확인 조회
 **********************************************************************/ 
function getogm203_cmbQorCd()
{

	var sKind		= "getogm203_cmbQorCds";						
	var sMethodName = "service::psogmlib/selectOGM203_cmbQorCd.do"; 
	var sInDataSet  = "";
