function lblCmplt_chan(){

	if(trim(txtCmplt.Text) == "1")
		lblCmplt.Text = "완료";
	else
		lblCmplt.Text = "미완료";
}

function txtCmplt_OnKillFocus(obj)
{
	lblCmplt_chan();
}

