function getogm204_obj210pf(){

	var sKind		= "getogm204_obj210pf";						
	var sMethodName = "service::psogmlib/selectOgm204_obj210pf.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_obj210pf=ds_obj210pf";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
//		sArgument += " JISNO=" + quote("PC2345"); //지시No
		sArgument += " JISNO=" + quote(trim(lbljisno.Text)); //지시No
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

