//필드값이 바뀔때마다 새로 asum,bsum,csum,dsum을 계산한다
function calcu_sums()
{
	lbla1hyo.Value = parseInt(txta1hyn.Text) - parseInt(txta1cst.Text);
	lbla2hyo.Value = parseInt(txta2hyo.Text);
	lbla3hyo.Value = parseInt(txta3hyn.Text) - parseInt(txta3cst.Text);
	lbla4hyo.Value = parseInt(txta4hyn.Text) -(parseInt(txta4cst.text) + parseInt(txta4aft.Text));
	lbla5hyo.Value = parseInt(txta5hyn.Text) - parseInt(txta5aft.Text);
	lbla6hyo.Value = parseInt(txta6hyn.Text) - (parseInt(txta6cst.text) + parseInt(txta6aft.Text));

	lblasum.Value = parseInt(lbla1hyo.Value) + parseInt(lbla2hyo.Value) + 
					parseInt(lbla3hyo.Value) + parseInt(lbla4hyo.Value) 
					+ parseInt(lbla5hyo.Value) + parseInt(lbla6hyo.Value) ;

	lblb1hyo.Value = (parseInt(txtb1hyn.Text)* gmanpower_loss_value) 
										-(parseInt(txtb1cst.text) 
										+ (parseInt(txtb1aft.Text) * gmanpower_loss_value));
	lblb2hyo.Value = (parseInt(txtb2hyn.Text)* gmanpower_loss_value) 
										-(parseInt(txtb2cst.text) 
										+ (parseInt(txtb2aft.Text)* gmanpower_loss_value));
	lblb3hyo.Value = (parseInt(txtb3hyn.Text)* gmanpower_loss_value)
										-(parseInt(txtb3cst.text) 
										+ (parseInt(txtb3aft.Text)* gmanpower_loss_value));
	lblb4hyo.Value = (parseInt(txtb4hyn.Text)* gmanpower_loss_value)
										- (parseInt(txtb4cst.text) 
										+ (parseInt(txtb4aft.Text)* gmanpower_loss_value)); 

	lblbsum.Value = parseInt(lblb1hyo.Value) + parseInt(lblb2hyo.Value) + 
					parseInt(lblb3hyo.Value) + parseInt(lblb4hyo.Value) ;

	lblc1hyo.Value = (parseInt(txtc1hyn.Text) * gline_loss_value) 
										-(parseInt(txtc1cst.text) 
										+ (parseInt(txtc1aft.Text) * gline_loss_value));
	lblc2hyo.Value = (parseInt(txtc2hyn.Text) * gline_loss_value) 
										-(parseInt(txtc2cst.text) 
										+ (parseInt(txtc2aft.Text) * gline_loss_value));
	lblc3hyo.Value = ((parseInt(txtc3hyn.Text) * parseInt(txtc3cst.text))/1000) 
										- ((parseInt(txtc3aft.Text) * parseInt(txtc3cst.text))/1000);
	lblc4hyo.Value = parseInt(txtc4hyn.Text) - (parseInt(txtc4cst.text)
										+ parseInt(txtc4aft.text));
	lblcsum.Value = parseInt(lblc1hyo.Value) + parseInt(lblc2hyo.Value) + 
					parseInt(lblc3hyo.Value) + parseInt(lblc4hyo.Value) ;

	lbld1hyo.Value = parseInt(txtd1hyn.Text) -(parseInt(txtd1cst.text) + parseInt(txtd1aft.Text));
	lbld2hyo.Value = parseInt(txtd2hyn.Text) -(parseInt(txtd2cst.text) + parseInt(txtd2aft.Text));
	lbld3hyo.Value = parseInt(txtd3hyn.Text) -(parseInt(txtd3cst.text) + parseInt(txtd3aft.Text));
	lbld5hyo.Value = parseInt(txtd5hyn.Text) - parseInt(txtd5aft.Text);
	lbld7hyo.Value = parseInt(txtd7hyn.Text) -(parseInt(txtd7cst.text) + parseInt(txtd7aft.Text));
	lbld8hyo.Value = parseInt(txtd8hyn.Text) - parseInt(txtd8aft.Text);
	lbldbhyo.Value = parseInt(txtdbhyn.Text) -(parseInt(txtdbcst.text) + parseInt(txtdbaft.Text));
	lbldchyo.Value = parseInt(txtdchyn.Text) - parseInt(txtdcaft.Text);
	lblddhyo.Value = parseInt(txtddhyn.Text) -(parseInt(txtddcst.text) + parseInt(txtddaft.Text));
	lbldehyo.Value = parseInt(txtdehyn.Text) - parseInt(txtdeaft.Text);
	lbldfhyo.Value = parseInt(txtdfhyn.Text) -(parseInt(txtdfcst.text) + parseInt(txtdfaft.Text));
	lbldghyo.Value = parseInt(txtdghyn.Text) -(parseInt(txtdgcst.text) + parseInt(txtdgaft.Text));
	lbldhhyo.Value = parseInt(txtdhhyn.Text) - parseInt(txtdhaft.Text);
	lbldihyo.Value = parseInt(txtdihyn.Text) -(parseInt(txtdicst.text) + parseInt(txtdiaft.Text));
	lbldjhyo.Value = parseInt(txtdjhyn.Text) - parseInt(txtdjcst.Text);
	lbldsum.Value = parseInt(lbld1hyo.Value) + parseInt(lbld2hyo.Value) + 
					parseInt(lbld3hyo.Value) + parseInt(lbld5hyo.Value) +
					parseInt(lbld7hyo.Value) + parseInt(lbld8hyo.Value) + 
					parseInt(lbldbhyo.Value) + parseInt(lbldchyo.Value) + 
					parseInt(lblddhyo.Value) + parseInt(lbldehyo.Value) + 
					parseInt(lbldfhyo.Value) + parseInt(lbldghyo.Value) + 
					parseInt(lbldhhyo.Value) + parseInt(lbldihyo.Value) + 
					parseInt(lbldjhyo.Value) ;

	var tsum = (parseInt(lblasum.Value) + parseInt(lblbsum.Value) +
					parseInt(lblcsum.Value) + parseInt(lbldsum.Value));
	//txttsum.value = formatparseInt(tsum.toString());
	txttsum.value = tsum;
}


