function calcu_OnKillFocus(obj)
{
	if (length(trim(obj.Text)) == 0)
	obj.Text = "0";
//alert(obj+"=="+obj.Text);
	calcu_sums();
}

function btnSave_OnClick(obj)
{
	if(length(trim(txtteima.Text)) == 0)
	{
		alert("테마를 입력해 주세요");
		return;
	}

	//효과금액이 1억을 넘는경우 체크
	if(trim(txttsum.Text).length > 3)
	{
		//if(Number(numberToDBformat(trim(txttsum.Text))) > 100000)
		if(parseInt(trim(txttsum.Text)) > 100000)
		{
			alert("효과금액이 1억원을 넘고 있습니다. 등록해도 괜찮습니까?\n효과단위는 '천원'입니다.");
		}
	}
/*
	getogm204_obj210pf();
	if(ds_obj210pf.GetColCount()!=0){
		alert("ok");
	}
	else {
		alert("NG");
	}
*/	
	getogm204_obj210pf();
	if(Confirm("저장하려면 '확인'을, 취소하려면 '취소'를 클릭하세요.")){
alert("ok");
		if(ds_obj210pf.GetColCount()!=0){
		var flag="update"; 
alert("flag====>"+flag);
			setogm204_obj210pf_insert(flag);	
		}
		else{
		var flag="insert"; 
alert("flag====>"+flag);
			setogm204_obj210pf_insert(flag);
			//var ECost = txtECost.Text
			var ecost = trim(txttsum.Text);
			txtECost.Text = ecost;
		}
	}
	else{
alert("NG");
		return;
	}
}


function brd4btnbfpadd_OnClick(obj) {

	getogm204_obj210pf_01();
	var file_name;;

	if(ds_obj210pf_01.GetColCount()!=0){
alert("ok");
		FileDialog0.Filter = "Image Files(*.*)|*.*|";	
		FileDialog0.Type = "OPEN";
		if(FileDialog0.Open()){

//			var file_name = FileDialog0.FileName;
			file_name = FileDialog0.FileName;
			var file_name = FileDialog0.FileName;
			var file_path = FileDialog0.FilePath;
			var path = file_path + "\\" + file_name;
			setMessageDirect(path);	
			brd4picbefor.ImageID=path;
			//ext_ExcelImportByIndex(path,0,"ds_main",0);
			//Grid0.CreateFormat(); 
			var lastdotpos;
			for(var i = 0; i< path.length; i++)	{
					if(path.CharAt(i) == ".")
						lastdotpos = i;
			}
			var	ext = path.substr(lastdotpos+1,3);
			var imgbf = trim(lbljisno.Text)+"_BFO."+ext;
				imgbf=gImgPath+imgbf;
alert("mgbf=======>"+imgbf);	
			//fileTempSave(path);
			//file_upload(obj);
			setogm204_obj210pf_preimg(imgbf);

		}
		else{
			alert("사진파일을 가져오지 않았습니다.");
		}
	}
	else {
		alert("개선테이블에 지시번호가 없습니다.\n\n먼저 개선데이타를 저장해 주세요");
	}	

}


