function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
//	ComboBoxPlant.Enable = false; 
}

function getDesign(){	
	//Grid0.ToolTipText = setPopMessage("2053");
	Grid0.BkColor = G_ds_design.GetColumn(0,"BkColor");
	Grid0.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	Grid0.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	Grid0.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	Grid0.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	Grid0.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	Grid0.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
 function getOgm216()
{
	var sKind		= "getOgm216";						
	var sMethodName = "service::psogmlib/selectOgm216.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm216=ds_ogm216";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(comps1); //사업부
		sArgument += " WNDATS=" + quote(wndates); //
		sArgument += " WNDATE=" + quote(wndatee); //
		sArgument += " MSHNO=" + quote(lblmshno.Text); //

	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
/*
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (ds_ogm216.rowcount() <= 0){
		alert(setPopMessage("1003"));
	}
*/	
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getOgm216"){
	// 조회건수가 없을경우
		if(ds_ogm216.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setOgm216"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm216_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_gm211.GetRowType(ds_ogm216.currow)=="insert"){
		ed_maker.Enable=true;
	}else{
		ed_maker.Enable=false;
	}
}


/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

