	txtstphr1.Text = "";
	txtnocnt1.Text = "";
*/	
	lblcallerboard.Text=lblcaller;
	txtwndats.Text = wndates;
	txtwndate.Text = wndatee;
	//txtstphr1.Text =stphr;
	//txtnocnt1.Text =stpcnt;

	if(lblcallerboard.Text=="ogm212"){

//	txtstphr1.Text =ds_ogm212.sum_stphr;
//	txtnocnt1.Text =ds_ogm212.cnt_stphr;
//	lblmshno.Text = trim(ds_ogm212.mshno);
//	txtmshnm.Text =trim(ds_ogm212.mshnm);
	txtstphr1.Text =ds_ogm212.GetColumn(ds_ogm212.currow,"SUM_STPHR");
	txtnocnt1.Text =ds_ogm212.GetColumn(ds_ogm212.currow,"CNT_STPHR");
	lblmshno.Text = trim(ds_ogm212.GetColumn(ds_ogm212.currow,"MSHNO"));
	txtmshnm.Text =trim(ds_ogm212.GetColumn(ds_ogm212.currow,"MSHNM"));
	}
	else if(lblcallerboard.Text=="ogm214"){
//	txtstphr1.Text =ds_ogm214.sum_stphr;
//	txtnocnt1.Text =ds_ogm214.cnt_stphr;
//	lblmshno.Text = trim(ds_ogm214.mshno);
//	txtmshnm.Text =trim(ds_ogm214.mshnm);

	txtstphr1.Text =ds_ogm214.GetColumn(ds_ogm214.currow,"SUM_STPHR");
	txtnocnt1.Text =ds_ogm214.GetColumn(ds_ogm214.currow,"CNT_STPHR");
	lblmshno.Text = trim(ds_ogm214.GetColumn(ds_ogm214.currow,"MSHNO"));
	txtmshnm.Text =trim(ds_ogm214.GetColumn(ds_ogm214.currow,"MSHNM"));
	}
	getOgm216();
}

