/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getogm313_ogm219"){
	// 조회건수가 없을경우
		if(ds_obj200pf_03.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setOgm211"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}
