function get_start_action(){
	getogm313_ogm219();
	get_inputData();
	getOgm219_inqParts();
	getOgm219_obj230pf();
	getOgm219_obj210pf();
}

function test_button(){

	if(ds_obj230pf.rowcount()!=0)  //첨부파일이 없다면
	{
		btncheomb.Enable = true;
	}
	else          //첨부파일이 있다면
	{
		btncheomb.Enable = false;
	}

	if(ds_obj210pf.rowcount()!=0)  //개선상세가 없다면
	{
		btneffdet.Enable= true;
	}
	else          //첨부파일이 있다면
	{
		btneffdet.Enable = false;
	}
}
