/********************************************************************** 
 * 내용설명 : 팀조회
 **********************************************************************/ 

function getogm310_cmbTimCd()
{
	var sKind		= "getogm310_cmbTimCd";						
	var sMethodName = "service::psogmlib/selectOgm211_timNm.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_obc290pf=ds_obc290pf";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync=true;;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	ds_obc290pf.InsertRow(0);
	ds_obc290pf.SetColumn(0,"TIMCD","");
	ds_obc290pf.SetColumn(0,"TIMNM","전체");
	cmbTimCd.Index=0;
}

function cmbWnDatS_OnKillFocus(obj)
{
	var sdate=substr(cmbWnDatS.Value,0,6)+"01";
	cmbWnDatS.Value=sdate;	
}

function cmbWnDatE_OnKillFocus(obj)
{
	var edate = substr(cmbWnDatE.Value,0,6);
	var ldate = addMonth(edate+"01", +1); //현재월01일 +1개월
	var vdate = addDate(ldate, -1);
	cmbWnDatE.Value=vdate;
}

/********************************************************************** 
 * 내용설명 : 합계구하기
 **********************************************************************/ 
function cal_data(){
/*
	var sum_stphr = 0.0;
	var sum_tothr = 0.0;
	var cnt_tothr = 0;
	var sum_bm = 0.0;
	var cnt_bm = 0;
	var sum_pm = 0.0;
	var cnt_pm = 0;
	var sum_cm = 0.0;
	var cnt_cm = 0;
	var sum_pcost = 0.0;
	var sum_scost = 0.0;
	var sum_ecost = 0.0;
	var cnt_repcd = 0;	
*/

	var sum_stphr = 0;
	var sum_tothr = 0;
	var cnt_tothr = 0;
	var sum_bm = 0;
	var cnt_bm = 0;
	var sum_pm = 0;
	var cnt_pm = 0;
	var sum_cm = 0;
	var cnt_cm = 0;
	var sum_pcost = 0;
	var sum_scost = 0;
	var sum_ecost = 0;
	var cnt_repcd = 0;	

	for (var i=0;i<ds_ogm310.GetRowCount();i++)
	{
		sum_stphr = sum_stphr+ parseFloat(ds_ogm310.getColumn(i,"SUM_STPHR"));
		sum_tothr = sum_tothr+ parseFloat(ds_ogm310.getColumn(i,"SUM_TOTHR"));
		cnt_tothr = cnt_tothr+ parseInt(ds_ogm310.getColumn(i,"CNT_TOTHR"));
		sum_bm = sum_bm+ parseFloat(ds_ogm310.getColumn(i,"SUM_BM"));
		cnt_bm = cnt_bm+ parseInt(ds_ogm310.getColumn(i,"CNT_BM"));
		sum_pm = sum_pm+ parseFloat(ds_ogm310.getColumn(i,"SUM_PM"));
		cnt_pm = cnt_pm+ parseInt(ds_ogm310.getColumn(i,"CNT_PM"));
		sum_cm = sum_cm+ parseFloat(ds_ogm310.getColumn(i,"SUM_CM"));
		cnt_cm = cnt_cm+ parseInt(ds_ogm310.getColumn(i,"CNT_CM"));
		sum_pcost = sum_pcost+ parseFloat(ds_ogm310.getColumn(i,"SUM_PCOST"));
		sum_scost = sum_scost+ parseFloat(ds_ogm310.getColumn(i,"SUM_SCOST"));
		sum_ecost = sum_ecost+ parseFloat(ds_ogm310.getColumn(i,"SUM_ECOST"));
		cnt_repcd = cnt_repcd+ parseInt(ds_ogm310.getColumn(i,"CNT_REPCD"));
	}
	//alert(sum_pm);
	txtStpHr.Text=NumFormat(sum_stphr,1);
	txtTotHr.Text=NumFormat(sum_tothr,1);
	txtNoCnt.Text=cnt_tothr;
	txtBmHr.Text=NumFormat(sum_bm,1);
	txtBmCnt.Text=cnt_bm;
	txtPmHr.Text=NumFormat(sum_pm,1);
	txtPmCnt.Text=cnt_pm;
	txtCmHr.Text=NumFormat(sum_cm,1);
	txtCmCnt.Text=cnt_cm;
	txtPCost.Text=NumFormat(sum_pcost,1);
	txtSCost.Text=NumFormat(sum_scost,1);
	txtECost.Text=NumFormat(sum_ecost,1);
	txtRepCnt.Text=cnt_repcd;
}
