/********************************************************************** 
 * 내용설명 : 설비No 입력시 asc code 체크
 **********************************************************************/ 
function chk_mshno(mshno)
{
	var msg = "";
	for(var i = 0 ; i < mshno.length; i++){
		var ch =Asc(mshno.CharAt(i));

		switch(i)
		{
			case 0:
			case 1:
				if((ch < 48) || (ch > 57 && ch < 65) || (ch > 90 && ch < 97) || (ch > 122))
				{	
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "1,2번째자리에는 숫자 또는 영문자가 와야 합니다";
					return msg;
				}
				break;
			case 2:
				if((ch != 32 && ch < 65) || (ch > 90 && ch < 97) || (ch > 122))   
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "3번째자리에는 영문자나 공란이 와야 합니다.";
					return msg;
				}
				break;
			case 3:
				if(ch != 32)
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "4번째자리는 공란이 와야 합니다.";
					return msg;
				}
				break;
			case 4:
			case 5:
			case 6:
			case 7:
				if(ch < 48 || ch > 57)
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "5 ~ 8번째짜리까지는 숫자가 와야 합니다.";
					return msg;
				}
				break;
			case 8:
			case 9:
				if((ch != 32 && ch < 48) || (ch  > 57 ))
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "8 ~ 10번째자리까지는 공란이나 숫자가 와야 합니다";
					return msg;
				}
				break;
			}
		}
	return msg;
}


