{
	if(length(txtjisno.Text)!=0){
		cmbprtcd.Text="";
		cmblincd.Text="";

		txtmshno.Text="";
		ckbBM.Value=false;
		ckbPM.Value=false;
		ckbCM.Value=false;
		cmbJobCd.Text="";

