		///=====		
		cmbprtcd.Enable=false;
		cmblincd.Enable=false;

		txtmshno.Enable=false;
		ckbBM.Enable=false;
		ckbPM.Enable=false;
		ckbCM.Enable=false;
		cmbJobCd.Enable=false;

		cmbsdpcd.Enable=false;
		txtwrkm1.Enable=false;
		cmbSubas.Enable=false;
		cmbComCd.Enable=false;
