		cmbHyoCd.Enable=true;
		cmbCauCd.Enable=true;
		cmbTruCd.Enable=true;
		lblAndOr.Enable=true;
		txthyosn1.Enable=true;
		txthyosn2.Enable=true;
		txthyosn3.Enable=true;
		txtwonin1.Enable=true;
		txtwonin2.Enable=true;
		txtwonin3.Enable=true;
		txtdaech1.Enable=true;
		txtdaech2.Enable=true;
		txtdaech3.Enable=true;
		Button0.Enable=true;

	}
}

function Button1_OnClick(obj)
{
	on_LoadOgm314();
}

function on_LoadOgm314(){

//----------------------------------체크박스처리부분
	var CheckedCount = parseInt(ckbBM.Value)+parseInt(ckbPM.Value)+parseInt(ckbCM.Value);//합이 3
	var rvalue = " (";
	var checking = 0;
	if (ckbBM.Value == 1){ //ckbBM.Value가 체크되어있으면 1...
		checking = checking+1;
		rvalue = rvalue+"'"+"BM"+"'";
	}
	if (ckbPM.Value == 1){
		if (checking != 0) rvalue = rvalue+",";
		checking = checking+1;
		rvalue = rvalue+"'"+"PM"+"'";
	}
	if (ckbCM.Value == 1){
		if (checking != 0) rvalue = rvalue+",";
		checking = checking+1;
		rvalue = rvalue+"'"+"CM"+"'";
	}
	rvalue = rvalue+")";

	var hyosn1 = txthyosn1.Text;
	var hyosn2 = txthyosn2.Text;
	//항목을 앞에서부터 채우기 위해..
	if(length(trim(hyosn1)) == 0)
	{
		hyosn1 = hyosn2;
		hyosn2 = "";
	}
	var hyosn3 = txthyosn3.Text;
	//항목을 앞에서부터 채우기 위해..
	if(length(trim(hyosn1)) == 0)
	{
		hyosn1 = hyosn3;
		hyosn3 = "";
	}
	else if(length(trim(hyosn2)) == 0)
	{
		hyosn2 = hyosn3;
		hyosn3 = "";
	}

	var wonin1 = txtwonin1.Text;
	var wonin2 = txtwonin2.Text;
	//항목을 앞에서부터 채우기 위해..
	if(length(trim(wonin1)) == 0)
	{
		wonin1 = wonin2;
		wonin2 = "";
	}

	var wonin3 = txtwonin3.Text;
	//항목을 앞에서부터 채우기 위해..
	if(length(trim(wonin1)) == 0)
	{
		wonin1 = wonin3;
		wonin3 = "";
	} 
	else if(length(trim(wonin2)) == 0)
	{
		wonin2 = wonin3;
		wonin3 = "";
	}

	var daech1 = txtdaech1.Text;
	//T(daech1);
	var daech2 = txtdaech2.Text;
	//항목을 앞에서부터 채우기 위해..
	if(length(trim(daech1)) == 0)
	{
		daech1 = daech2;
		daech2 = "";
	}
	var daech3 = txtdaech3.Text;
	//항목을 앞에서부터 채우기 위해..
	if(length(trim(daech1)) == 0)
	{
		daech1 = daech3;
		daech3 = "";
	}
	else if(length(trim(daech2)) == 0)
	{
		daech2 = daech3;
		daech3 = "";
	}	
	var sKind		= "getOgm314_onload";						
	var sMethodName = "service::psogmlib/selectOgm314_onload.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm314=ds_ogm314";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@PRTCD=" + quote(trim(cmbprtcd.Value));
		sArgument += "@LINCD=" + quote(trim(cmblincd.Value));
		sArgument += "@MSHNO=" + quote(trim(txtmshno.Text));
		sArgument += "@JOBCD=" + quote(cmbJobCd.Value);
		sArgument += "@KOGUBCHK=" + quote(parseInt(CheckedCount));
		sArgument += "@KOGUB=" + quote(rvalue);
		sArgument += "@SDPCD=" + quote(cmbsdpcd.Value);
		sArgument += "@WRKM1=" + quote(txtwrkm1.Text);
		sArgument += "@SUBAS=" + quote(trim(cmbSubas.Value));
		sArgument += "@COMCD=" + quote(trim(cmbComCd.Value));
		sArgument += "@DECCD=" + quote(trim(cmbDecCd.Value));
		sArgument += "@CHOCD=" + quote(trim(cmbChoCd.Value));
		sArgument += "@HYOCD=" + quote(trim(cmbHyoCd.Value));
		sArgument += "@CAUCD=" + quote(trim(cmbCauCd.Value));
		sArgument += "@TRUCD=" + quote(trim(cmbTruCd.Value));
		sArgument += "@ANDOR=" + quote(trim(lblAndOr.Text));
		sArgument += "@HYOSN1=" + quote(hyosn1);
		sArgument += "@HYOSN2=" + quote(hyosn2);
		sArgument += "@HYOSN3=" + quote(hyosn3);
		sArgument += "@WONIN1=" + quote(wonin1);
		sArgument += "@WONIN2=" + quote(wonin2);
		sArgument += "@WONIN3=" + quote(wonin3);
		sArgument += "@DAECH1=" + quote(daech1);
		sArgument += "@DAECH2=" + quote(daech2);
		sArgument += "@DAECH3=" + quote(daech3);
		sArgument += "@WNDATS=" + quote(dtpwndats.Value);
		sArgument += "@WNDATE=" + quote(dtpwndate.Value);

	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	var str = dialog("psogmlib::ogm314.xml","comps1="+ComboBoxPlant.Value,300,300, "TitleBar=false");	
}
