function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	lblsdpcd.Text = cmbsdpcd.Value;
	lblsdpnm.Text = cmbsdpcd.Text;
	lblselyear.Text = selyear.Text;
	//getComCode(); //공장구분 선택
	getOgm323();
	//gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
//	ComboBoxPlant.Enable = false; 
}

function getDesign(){	
	Grid_ogm323.ToolTipText = setPopMessage("2053");
	Grid_ogm323.BkColor = G_ds_design.GetColumn(0,"BkColor");
	Grid_ogm323.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	Grid_ogm323.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	Grid_ogm323.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	Grid_ogm323.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	Grid_ogm323.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
function getOgm323(){

	var sdpcd="";
	var sdpcdVal=trim(cmbsdpcd.Value);
	if(length(sdpcdVal)!=0){
		sdpcd=replace(sdpcdVal,"*","%");
	}

	var sKind		= "getOgm323";						
	var sMethodName = "service::psogmlib/selectOgm323.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm323=ds_ogm323";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " SDPCD=" + quote(sdpcd);
		//sArgument += " EFFCD=" + quote(trim(lbleffcd.Text));
		sArgument += " WNDATS=" + quote(trim(lblselyear.Text)+"0401"); //검색기간START
		sArgument += " WNDATE=" + quote(parseInt(trim(lblselyear.Text))+1+"0331"); //검색기간END
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (ds_ogm323.rowcount() <=0){
		alert(setPopMessage("1003"));
	}
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "효과구분별,월별 효과금액";
	var arrSheets  =  Array();  

	arrSheets[0]  = "Grid_ogm323|효과구분별,월별 효과금액";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}
