function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode(); //공장구분 선택

	getogmGauge_mst650();
	dtpbasedat.Value=nowDate;
	GroupBox1.Value="a.group";

	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;

}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	http.Sync=false;
	ComboBoxPlant.Value=comps;
//	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
	static19.Width = G_ds_design.GetColumn(0,"Width");
	Static7.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
function getOgm422(){

	var isselall = "";

	if (chkisselall.Value==1){
		isselall = "Y";
	}
	else if	(chkisselall.Value==0 && dtpbasedat.Value==""){
		//alert("기준일자를 입력해주세요.");
		alert(setPopMessage("2571"));
		return;
	}
	var chkVal = chk_box();
//alert(chkVal);	
	var CheckedCount = chkVal[0];
	var rvalue = chkVal[1];
//alert(CheckedCount);	
//alert(rvalue);	

	var sKind		= "getOgm422";						
	var sMethodName = "service::psogmlib/selectOgm422.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm422=ds_ogm422";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);		//Lib
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);			//사업부
		//sArgument += "@CHKDISYN=" + quote(grbdisyn.Value);	//폐기여부
		sArgument += "@DPTCD=" + quote(cmbdptcd.Value);	//부서
		sArgument += "@RPDATE=" + quote(dtpbasedat.Value);	//교정년도end
		sArgument += "@ISSELALL=" + quote(isselall);	//정렬순서
		sArgument += "@SELORDERBY=" + quote(GroupBox1.Value);	//정렬순서
		sArgument += "@CHKCOUNT=" + quote(parseInt(CheckedCount));
		sArgument += "@CHKVALUE=" + quote(rvalue);
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : 소속조회
 **********************************************************************/ 
function getogmGauge_mst650()
{
	var sKind		= "getogmGauge_mst650";						
	var sMethodName = "service::psogmlib/selectOGMgauge_Mst650pf.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_mst650pf=ds_mst650pf";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm200"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm422_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_ogm422.GetRowType(ds_ogm421.currow)=="insert"){
		ed_maker.Enable=true;
	}else{
		ed_maker.Enable=false;
	}
}

