#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
//var Lib2 = "PSTSTLIB"; // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

/********************************************************************** 
 * 내용설명 : 초기화면
 **********************************************************************/ 
function on_LoadCompleted(obj){

	getTitle();	
	getDesign();
	getComCode(); //공장구분 선택
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	

	MainGrid.UserData = MainGrid.Height;
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}



function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
	displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getogm430(){

	//조회할때는 코드입력창이 비활성화 되어야 한다
	displayDiv.ed_mstcd1.Enable="false";

	var sKind		= "getogm430";						
	var sMethodName = "service::psogmlib/selectOGM430.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm430=ds_ogm430";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);	
	sArgument += "@GRPCD=" + quote(cb_grpcd.Value);      //코드분류
	sArgument += "@MSTCD=" + quote(ed_mstcd.Value);      //코드

	var str = dialog(
	                 "Common::PGMLOAD.xml",
	                 "sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + 
	                 "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,
	                 272,             //넓이
	                 64,              //높이
	                 "TitleBar=false" //창의 이동 금지
	                 );
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	  
	}	               
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setogm430(){

	//필수입력 체크(저장버튼 클릭시)
	if(displayDiv.ed_mstcd1.Value == ""){
		//alert("필수입력항목입니다. (코드)");
		alert(setPopMessage("2571"));
		displayDiv.ed_mstcd1.SetFocus();
		return; 
	}

	if(displayDiv.ed_mstsm.Value == ""){
		//alert("필수입력항목입니다. (코드약명)");
		alert(setPopMessage("2572"));
		displayDiv.ed_mstsm.SetFocus();
		return; 
	}

	if(displayDiv.ed_mstrm.Value == ""){
		//alert("필수입력항목입니다. (코드원명)");
		alert(setPopMessage("2573"));
		displayDiv.ed_mstrm.SetFocus();
		return; 
	}

	// 중복check - 로우타입이 Insert일때만 중복체크
	if(ds_ogm430.GetRowType(ds_ogm430.currow) == "insert"){
		if(!duplicateChk()){         
			displayDiv.ed_mstcd1.SetFocus();
			return false;
		}
	}

	var sKind		= "setogm430";						
	var sMethodName = "service::psogmlib/OGM430Save.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm430=ds_ogm430:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);  //유저아이디
	sArgument += " ADUSR=" + quote(idnum);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);	
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;

	//getogm430();
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm430"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}


/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크(추가처리 중 기존 로우 선택했을 때 활성화유무)
 **********************************************************************/ 
function ds_ogm430_OnRowPosChanged(obj,nOldRow,nRow){

	//로우타입 insert만 ED박스 enable
	if(ds_ogm430.GetRowType(ds_ogm430.currow)=="insert"){
		displayDiv.ed_mstcd1.Enable=true;
	}else{
		displayDiv.ed_mstcd1.Enable=false;
	}
}

/********************************************************************** 
 * 내용설명 : 중복 체크
 **********************************************************************/ 
function duplicateChk(){

	http.Sync 	= true;
	var sKind		= "getogm430_dchk";						
	var sMethodName = "service::psogmlib/selectOGM430_dchk.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_OGM430_dchk=ds_OGM430_dchk";
	var sArgument   = "";

	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);	
	sArgument += " GRPCD=" + quote(displayDiv.ed_grpcd.Value);       //코드분류
	sArgument += " MSTCD=" + quote(displayDiv.ed_mstcd1.Value);      //코드	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");

	//검색결과가 있으면 중복메세지 표시
	if(ds_OGM430_dchk.rowcount() > 0){
		alert(setPopMessage("2039"));	// 2039 == 중복된data가 존재합니다..
		return false;
	}
	return true;	
}

/********************************************************************** 
 * 내용설명 : validation 입력체크 (수정의 경우만 해당, 신규등록은 저장버튼에서 처리)
 **********************************************************************/ 
function ds_ogm430_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	// if( strColumnID == "MSTCD" ){
		// if( length(varNewValue) == 0 ){
			// alert("필수입력항목입니다. (코드)");
			// return false;
		// }
	// } 

	if( strColumnID == "MSTSM" ){
		if( length(varNewValue) == 0 ){
			//alert("필수입력항목입니다. (코드약명)");
			alert(setPopMessage("2572"));
			return false;
		}
	}

	if( strColumnID == "MSTRM" ){
		if( length(varNewValue) == 0 ){
			//alert("필수입력항목입니다. (코드원명)");
			alert(setPopMessage("2573"));
			return false;
		}
	}
	return true;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "코드관리";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|코드관리";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : 추가버튼 처리
 **********************************************************************/ 
function setFn_Input(){

	//검색시 그 결과값이 없을 때 추가ㅣ버튼을 누르면 로우가 추가되지 않기에 강제추가
	if(ds_ogm430.GetRowCount() < 1){

		ds_ogm430.AddColumn("ROWNUM");
		ds_ogm430.AddColumn("COMPS");
		ds_ogm430.AddColumn("GRPCD");
		ds_ogm430.AddColumn("MSTCD");
		ds_ogm430.AddColumn("MSTSM");
		ds_ogm430.AddColumn("MSTRM");
		ds_ogm430.AddColumn("INPDT");
		ds_ogm430.AddColumn("INPMN");
		ds_ogm430.AddColumn("CHGDT");
		ds_ogm430.AddColumn("CHGMN");
	}

	//로우추가 - 데이터가 있으면 그냥 로우 추가만
	ds_ogm430.AddRow();

	ds_ogm430.SetColumn(ds_ogm430.Row, "ROWNUM", ds_ogm430.RowCount()); // default값 설정		

	//추가할때는 코드입력창이 활성화 되어야 한다
	displayDiv.ed_mstcd1.Enable=true;

	//변경할 레코더 인덱스, 컬럼아이디, 컬럼값
	ds_ogm430.SetColumn(ds_ogm430.Row, "GRPCD", cb_grpcd.value); // default값 설정		
	displayDiv.ed_mstcd1.SetFocus();

}


