#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
//var Lib2 = "PSTSTLIB";
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode(); //공장구분 선택
	SetDN(obj,ds_DN); //주야구분
	cmbDN.Index = 0;
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getogm503(){

	var sKind		= "getOgm503";						
	var sMethodName = "service::psogmlib/selectogm503.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm503=ds_ogm503";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@DAYGU=" + quote(cmbDN.Value); 
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setogm503(){
	if(!validationChk()) return;  //추가버튼click후 바로 저장시를 위하여
	var sKind		= "setogm503";						
	var sMethodName = "service::psogmlib/ogm503Save.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm503=ds_ogm503:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " DAYGU=" + quote(cmbDN.Value); 
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm503"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm503_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_ogm503.GetRowType(ds_ogm503.currow)=="insert"){
		displayDiv.ed_WRKGU.Enable=true;
	}else{
		displayDiv.ed_WRKGU.Enable=false;
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function ds_ogm503_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if( strColumnID == "WRKGU" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2553"));   // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			return false;
		}
		//중복Check
		http.usewaitcursor = false;
		HTTP.Sync = true;
		var sKind		= "getogm503_Chk";
		var sMethodName = "service::psogmlib/selectogm503_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm503_Chk=ds_ogm503_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " DAYGU=" + quote(cmbDN.Value); //주야간 구분
		sArgument += " WRKGU=" + quote(varNewValue); // 구분
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.usewaitcursor = true;
		http.Sync = false;
		if (ds_ogm503_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533")); //이미등록된코드입니다. 다른코드를 입력하세요!
			setMessage("2533");
			return false;
		}
	}
	if( strColumnID == "WRKST" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2553"));     // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			return false;
		}
	}
	if( strColumnID == "WRKED" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2553"));     // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			return false;
		}
	}
	if( strColumnID == "WRKTM" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2553"));     // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			return false;
		}
	}
	return true;
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<ds_ogm503.rowcount(); i++){ 
		if(length(trim(ds_ogm503.GetColumn(i, "WRKGU")))==0){
			alert(setPopMessage("2553"));	 // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			ds_ogm503.row = i;
			return false;
		}
		if(length(trim(ds_ogm503.GetColumn(i, "WRKST")))==0){
			alert(setPopMessage("2553"));	 // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			ds_ogm503.row = i;
			return false;
		}
		if(length(trim(ds_ogm503.GetColumn(i, "WRKED")))==0){
			alert(setPopMessage("2553"));	 // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			ds_ogm503.row = i;
			return false;
		}
		if(length(trim(ds_ogm503.GetColumn(i, "WRKTM")))==0){
			alert(setPopMessage("2553"));	 // [경고] 필수입력항목입니다.(구분,시작시간,종료시간,작업시간)
			setMessage("2553");
			ds_ogm503.row = i;
			return false;
		}
	}
	return true;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "표준시간";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|표준시간";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

