function fn_Input()
{
	if(checkInsertCount("ds_Ogm530")== false) return;
	ds_Ogm530.AddRow();
}

function fn_Delete()
{
	ds_Ogm530.DeleteRow(ds_Ogm530.currow);
}

function fn_Save()
{
	setOgm530();
}

function fn_print()
{
	alert("fn_print");
}

