}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_Ogm530.DeleteAll();
}

function ComboBoxPlant_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	SetDevID(Lib2, strCode); //장치명 선택
	cmbDEVID.Index = 0;
	ds_Ogm530.DeleteAll();

	getGridTitleInitial();
	GetTime(,,ComboBoxPlant.Value,cmbDN.Value);
	var cnt = ds_QRTime.GetRowCount();
	arrTime = Array(cnt);
	var arrTimeE;
	for (var i=0 ; i<cnt ; i++){
		arrTime[i] = ds_QRTime.GetColumn(i,"WRKST").substr(0,2) + ":" + ds_QRTime.GetColumn(i,"WRKST").substr(2,2) + "\n~" + ds_QRTime.GetColumn(i,"WRKED").substr(0,2) + ":" + ds_QRTime.GetColumn(i,"WRKED").substr(2,2) ;
		MainGrid.SetCellProp("Head", i+5, "text", arrTime[i]);
	}

}

/********************************************************************** 
 * 내용설명 : 상세화면 조회
 **********************************************************************/ 
function detail_View(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_Ogm530.rowcount <= 0 || ds_Ogm530.GetRowType(ds_Ogm530.row) != 'normal') return ;
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ds_Ogm530.GetColumn(ds_Ogm530.row,"COMPS"));
		sArgument += " HOUSE=" + quote(ds_Ogm530.GetColumn(ds_Ogm530.row,"HOUSE"));
		sArgument += " DEVID=" + quote(ds_Ogm530.GetColumn(ds_Ogm530.row,"DEVID"));
		sArgument += " PSPNO=" + quote(ds_Ogm530.GetColumn(ds_Ogm530.row,"PSPNO"));
		sArgument += " SDATE=" + quote(Calendar0.Value);
		sArgument += " DAYGU=" + quote(cmbDN.Value);
		sArgument += " DEBNM=" + quote(ds_Ogm530.GetColumn(ds_Ogm530.row,"DEBNM"));
	var str = dialog("PSOGMLIB::ogm530d.xml",sArgument,1100,600);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);

	// Ctrl + V
	/*} else if(bCtrl == 1 && nChar == 86) {

		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardPaste(obj, strSeparator);

	// Ctrl + Z
	} else if(bCtrl == 1 && nChar == 90) {

		// grd_Standard.AreaSelect = true;
		grd_fn_CellReset(obj);*/
	}
}
