var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1");
var Lib2 = G_ds_user.GetColumn(0,"LIB2");
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();


/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)		
	ca_plnymF.Value = nowDate;
	ca_plnymT.Value = AddMonth(ca_plnymF.Value,11);
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
}

function getComCode(){
// KR용 영업지원시스템 : KA,KE사업부
	setCompanyName(ds_ComCode_PLNT);
	cb_searchCodeName.Value="PS";
	if (cb_searchCodeName.Value == "PS"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
	} else if(cb_searchCodeName.Value == "PE"){
		Lib1 = "PERESLIB";
		Lib2 = "PSEDBLIB";
	}
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getR_SPYZ906Q()
{
	var month1 = substr(AddMonth(ca_plnymF.Value,1),0,6);
	var month2 = substr(AddMonth(ca_plnymF.Value,2),0,6);
	var month3 = substr(AddMonth(ca_plnymF.Value,3),0,6);
	var month4 = substr(AddMonth(ca_plnymF.Value,4),0,6);
	var month5 = substr(AddMonth(ca_plnymF.Value,5),0,6);
	var month6 = substr(AddMonth(ca_plnymF.Value,6),0,6);
	var month7 = substr(AddMonth(ca_plnymF.Value,7),0,6);
	var month8 = substr(AddMonth(ca_plnymF.Value,8),0,6);
	var month9 = substr(AddMonth(ca_plnymF.Value,9),0,6);
	var month10 = substr(AddMonth(ca_plnymF.Value,10),0,6);
	MainGrid.SetCellProp("Head", 8, "text", substr(ca_plnymF.Value,0,4)+"/"+substr(ca_plnymF.Value,4,2));
	MainGrid.SetCellProp("Head", 9, "text", substr(month1,0,4)+"/"+substr(month1,4,2));
	MainGrid.SetCellProp("Head", 10, "text", substr(month2,0,4)+"/"+substr(month2,4,2));
	MainGrid.SetCellProp("Head", 11, "text", substr(month3,0,4)+"/"+substr(month3,4,2));
	MainGrid.SetCellProp("Head", 12, "text", substr(month4,0,4)+"/"+substr(month4,4,2));
	MainGrid.SetCellProp("Head", 13, "text", substr(month5,0,4)+"/"+substr(month5,4,2));
	MainGrid.SetCellProp("Head", 14, "text", substr(month6,0,4)+"/"+substr(month6,4,2));
	MainGrid.SetCellProp("Head", 15, "text", substr(month7,0,4)+"/"+substr(month7,4,2));
	MainGrid.SetCellProp("Head", 16, "text", substr(month8,0,4)+"/"+substr(month8,4,2));
	MainGrid.SetCellProp("Head", 17, "text", substr(month9,0,4)+"/"+substr(month9,4,2));
	MainGrid.SetCellProp("Head", 18, "text", substr(month10,0,4)+"/"+substr(month10,4,2));
	MainGrid.SetCellProp("Head", 19, "text", substr(ca_plnymT.Value,0,4)+"/"+substr(ca_plnymT.Value,4,2));

	var p_pyvnd1,p_pyvnd2;
	p_pyvnd1 = iif (length(trim(ed_pyvnd_from.Text)) == 0, '0000', trim(ed_pyvnd_from.Text));
	p_pyvnd2 = iif (length(trim(ed_pyvnd_to.Text)) == 0 , '9999', trim(ed_pyvnd_to.Text));

	var sKind		= "getR_SPYZ906Q";						
	var sMethodName = "service::pssallib/selectR_SPYZ906Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_R_SPYZ906Q=ds_R_SPYZ906Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
    sArgument += "@PLNYM_F=" + quote(substr(ca_plnymF.Value,0,6)); //시작계획년월 
    sArgument += "@PLNYM_T=" + quote(substr(ca_plnymT.Value,0,6)); //종료계획년월 
    sArgument += "@PLNYM_1=" + quote(month1); //계획년월+1 
    sArgument += "@PLNYM_2=" + quote(month2); //계획년월+2
    sArgument += "@PLNYM_3=" + quote(month3); //계획년월+3
    sArgument += "@PLNYM_4=" + quote(month4); //계획년월+4
    sArgument += "@PLNYM_5=" + quote(month5); //계획년월+5 
    sArgument += "@PLNYM_6=" + quote(month6); //계획년월+6 
    sArgument += "@PLNYM_7=" + quote(month7); //계획년월+7
    sArgument += "@PLNYM_8=" + quote(month8); //계획년월+8 
    sArgument += "@PLNYM_9=" + quote(month9); //계획년월+9
    sArgument += "@PLNYM_10=" + quote(month10); //계획년월+10
    sArgument += "@PLNGB=" + quote(cb_plngb.Value); //계획구분
    sArgument += "@PYVND1=" + quote(p_pyvnd1);
	sArgument += "@PYVND2=" + quote(p_pyvnd2);	

	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = substr(ca_plnymF.Value,0,6)+"년판매계획대실적";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|년판매계획대실적";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else if(svcId=="getR_SPYZ906Q"){
		// 조회건수가 없을경우
		if(ds_R_SPYZ906Q.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}	
	}
}

