function detail_View(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_R_SPYZ906Q.rowcount <= 0 || ds_R_SPYZ906Q.GetRowType(ds_R_SPYZ906Q.row) != 'normal') return ;

	var plantcd;
	var selected_comp;

	if(cb_comp.Value == "") {
		selected_comp="ZZ";
	}else {
		selected_comp=cb_comp.Value;
	}

	if(cb_plant.Value == "") {
		plantcd="ZZ";
	}else {
		plantcd=cb_plant.Value;
	}
	var str = dialog("PSSALLIB::R_SPYZ906QD.xml","P_COMPS=" + selected_comp + " " + "P_PLTCD=" + plantcd + " " + "P_PRDCD=" + ds_R_SPYZ906Q.GetColumn(ds_R_SPYZ906Q.row,"V")+ " " + "P_YYMMF=" + SubStr(ca_plnymF.Value,0,6) + " " + "P_YYMMT=" + SubStr(ca_plnymT.Value,0,6) + " " + "P_PLNGB=" + cb_plngb.Value,1100,600);
	trace (str);
}

function ca_plnymF_OnChanged(obj,strOldText,strNewText)
{
	ca_plnymT.Value = AddMonth(ca_plnymF.Value,11);
	ds_R_SPYZ906Q.DeleteAll();
}

function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_R_SPYZ906Q.DeleteAll();
}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}

function cb_searchCodeName_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "PS"){
		LIB1 = "PSRESLIB";
		LIB2 = "PSCDBLIB";
	} else if(strCode == "PE"){
		LIB1 = "PERESLIB";
		LIB2 = "PSEDBLIB";
	}
	ds_R_SPYZ906Q.ClearData();
}
