var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1.3"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	//searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSale156(){

	if(!validationChk()){
		return;
	}


	var ePrtNoVal="";
	var sDateVal="";
	var eDateVal="";
	if(length(ed_SVNM.Text)==0){
		ed_SUBSVNM="00000000";
	}
	else{
		ed_SUBSVNM=ed_SVNM.Text;
	}	
	if(length(ed_EVNM.Text)==0){
		ed_SUBEVNM="99999999";
	}
	else{
		ed_SUBEVNM=ed_EVNM.Text;
	}
	if(length(ed_EPRTNO.Text)==0){
		ePrtNoVal="999999999999999";
	}
	else{
		ePrtNoVal=ed_EPRTNO.Text;
	}		
	if(cmb_SDATE.Value == ""){
		sDateVal="00000000";
	}
	else if(cmb_SDATE.Value != ""){
		sDateVal=cmb_SDATE.Value;
	}
	if(cmb_EDATE.Value == ""){
		eDateVal="99999999";
	}
	else if(cmb_EDATE.Value != ""){
		eDateVal=cmb_EDATE.Value;
	}

	var sKind		= "getSale156";						
	var sMethodName = "service::pssallib/selectSale156.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE156=ds_SALE156";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " SVNM=" + quote(ed_SUBSVNM.Text); //업체
		sArgument += " EVNM=" + quote(ed_SUBEVNM.Text); //업체
		sArgument += " SPRTNO=" + quote(ed_SPRTNO.Text); //품번
		sArgument += " EPRTNO=" + quote(ePrtNoVal); //품번
		sArgument += " SDATE=" + quote(sDateVal); //적용일
		sArgument += " EDATE=" + quote(eDateVal); //적용일
		sArgument += " PCHK=" + quote(cmb_PCHK.Value); //출력구분
	//var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	if(ds_SALE156.GetRowCount() <=0){
		alert(setPopMessage("1003"));
		setMessage("1003");
		return;
	}
	dialog("pssallib::SALE156CP_rep.xml", "",300,300,"TitleBar=true",-1,0);

/*	
	if (str == 'T'){  //정상조회
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
*/	
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var sdVal = cmb_SDATE.Value;
	var edVal = cmb_EDATE.Value;

	if (sdVal == "" || edVal == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	else if(sdVal > edVal)
	{
		alert(setPopMessage("3049"));
		result =false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_SALE156.DeleteAll();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "제품가단가 관리 현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|제품가단가 관리 현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

