function fn_Input()
{
	if(!validationChk()){
		return;
	}

	if(checkInsertCount("ds_SALE160")== false) return;
	if(ds_SALE160.rowcount < 1) fn_Search();  
	ds_SALE160.AddRow();
	ds_SALE160.SetColumn(ds_SALE160.currow, "COMPS", ComboBoxPlant.Value);
	ds_SALE160.SetColumn(ds_SALE160.currow, "PYVND", ed_PYVND.Text);	
	ds_SALE160.SetColumn(ds_SALE160.currow, "DAT01", 0);	
	ds_SALE160.SetColumn(ds_SALE160.currow, "AEF01", "");	
	ds_SALE160.SetColumn(ds_SALE160.currow, "PER01", 0);	
	ds_SALE160.SetColumn(ds_SALE160.currow, "DAT02", 0);	
	ds_SALE160.SetColumn(ds_SALE160.currow, "AEF02", "");	
	ds_SALE160.SetColumn(ds_SALE160.currow, "PER02", 0);	
	ds_SALE160.SetColumn(ds_SALE160.currow, "DAT03", 0);	
	ds_SALE160.SetColumn(ds_SALE160.currow, "AEF03", "");	
	ds_SALE160.SetColumn(ds_SALE160.currow, "PER03", 0);	

//trace(ds_SALE160.SaveXML());
}

function fn_Delete()
{
	if(ds_SALE160.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_SALE160.DeleteRow(ds_SALE160.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

