function imgMaker_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_PYVND.Text);
	if (length(trim(ed_PYVND.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "B0";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_PYVND.Text = rtn[0];
		st_PYVND.Text = rtn[1];
	}
}

function ed_PYVND_OnCharChanged(obj,strPreText,strPostText)
{
	st_PYVND.Text = "";
}

function ed_PYVND_OnChanged(obj,strText)
{
	ds_SALE160.ClearData();
}

function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("4671"));
		setMessage("4671");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;	
}
function ds_SALE160_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="CLHCT"){//출하횟수 check
		if(varNewValue!=""){
			if(varNewValue <1 || varNewValue > 3){
				alert("[알림]출하횟수는 3회까지만 가능합니다.");
				//alert(setPopMessage("4734"));//[알림]출하횟수는 4회까지만 가능합니다.
				//setMessage("4734");	
				return false;		
			}
		}
	}			
}

function ds_SALE160_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="CLHCT"){//출하횟수 check
		if(varNewValue==1){
			MainGrid.SetCellProp("body",7,"Edit","none");
			MainGrid.SetCellProp("body",8,"Edit","none");
			MainGrid.SetCellProp("body",9,"Edit","none");
			MainGrid.SetCellProp("body",10,"Edit","none");
			MainGrid.SetCellProp("body",11,"Edit","none");
			MainGrid.SetCellProp("body",12,"Edit","none");
			ds_SALE160.SetColumn(nRow,"DAT02",0);
			ds_SALE160.SetColumn(nRow,"AEF02","");
			ds_SALE160.SetColumn(nRow,"PER02",0);
			ds_SALE160.SetColumn(nRow,"DAT03",0);
			ds_SALE160.SetColumn(nRow,"AEF03","");
			ds_SALE160.SetColumn(nRow,"PER03",0);			
		}
		else if(varNewValue==2){
			MainGrid.SetCellProp("body",7,"Edit","number");
			MainGrid.SetCellProp("body",8,"Edit","upperchar");
			MainGrid.SetCellProp("body",9,"Edit","number");
			MainGrid.SetCellProp("body",10,"Edit","none");
			MainGrid.SetCellProp("body",11,"Edit","none");
			MainGrid.SetCellProp("body",12,"Edit","none");
			ds_SALE160.SetColumn(nRow,"DAT03",0);
			ds_SALE160.SetColumn(nRow,"AEF03","");
			ds_SALE160.SetColumn(nRow,"PER03",0);	
		}
		else if(varNewValue==3){
			MainGrid.SetCellProp("body",7,"Edit","number");
			MainGrid.SetCellProp("body",8,"Edit","upperchar");
			MainGrid.SetCellProp("body",9,"Edit","number");
			MainGrid.SetCellProp("body",10,"Edit","number");
			MainGrid.SetCellProp("body",11,"Edit","upperchar");
			MainGrid.SetCellProp("body",12,"Edit","number");
		}				
	}
	if(strColumnID=="DAT01"){
		if(varNewValue==""){
			ds_SALE160.SetColumn(nRow,"DAT01",0);
		}			
	}
	if(strColumnID=="DAT02"){
		if(varNewValue==""){
			ds_SALE160.SetColumn(nRow,"DAT02",0);
		}			
	}
	if(strColumnID=="DAT03"){
		if(varNewValue==""){
			ds_SALE160.SetColumn(nRow,"DAT03",0);
		}			
	}			
}


/********************************************************************** 
 * 내용설명 : Grid의 구분 값에 따른  필드(row변경시 발생)
 **********************************************************************/ 
function ds_SALE160_OnRowPosChanged(obj,nOldRow,nRow)
{
	if(ds_SALE160.GetColumn(ds_SALE160.currow, "CLHCT")==1){
			MainGrid.SetCellProp("body",7,"Edit","none");
			MainGrid.SetCellProp("body",8,"Edit","none");
			MainGrid.SetCellProp("body",9,"Edit","none");
			MainGrid.SetCellProp("body",10,"Edit","none");
			MainGrid.SetCellProp("body",11,"Edit","none");
			MainGrid.SetCellProp("body",12,"Edit","none");
	}else if(ds_SALE160.GetColumn(ds_SALE160.currow, "CLHCT")==2){
			MainGrid.SetCellProp("body",7,"Edit","number");
			MainGrid.SetCellProp("body",8,"Edit","upperchar");
			MainGrid.SetCellProp("body",9,"Edit","number");
			MainGrid.SetCellProp("body",10,"Edit","none");
			MainGrid.SetCellProp("body",11,"Edit","none");
			MainGrid.SetCellProp("body",12,"Edit","none");
	}else if(ds_SALE160.GetColumn(ds_SALE160.currow, "CLHCT")==3){
			MainGrid.SetCellProp("body",7,"Edit","number");
			MainGrid.SetCellProp("body",8,"Edit","upperchar");
			MainGrid.SetCellProp("body",9,"Edit","number");
			MainGrid.SetCellProp("body",10,"Edit","number");
			MainGrid.SetCellProp("body",11,"Edit","upperchar");
			MainGrid.SetCellProp("body",12,"Edit","number");
	}

}
function ds_SALE160_CanRowPosChange(obj,nOldRow,nRow)
{
	if(ds_SALE160.GetRowType(ds_SALE160.currow)=="update" ||ds_SALE160.GetRowType(ds_SALE160.currow)=="insert" ){
		var makcr=ds_SALE160.GetColumn(ds_SALE160.currow,"MAKCR");
		var imfdt=ds_SALE160.GetColumn(ds_SALE160.currow,"IMFDT");
		var upddt=ds_SALE160.GetColumn(ds_SALE160.currow,"UPDDT");
		var clhct=ds_SALE160.GetColumn(ds_SALE160.currow,"CLHCT");
		var dat01=ds_SALE160.GetColumn(ds_SALE160.currow,"DAT01");
		var aef01=ds_SALE160.GetColumn(ds_SALE160.currow,"AEF01");	
		var per01=parseInt(ds_SALE160.GetColumn(ds_SALE160.currow,"PER01"));
		var dat02=ds_SALE160.GetColumn(ds_SALE160.currow,"DAT02");
		var aef02=ds_SALE160.GetColumn(ds_SALE160.currow,"AEF02");	
		var per02=parseInt(ds_SALE160.GetColumn(ds_SALE160.currow,"PER02"));
		var dat03=ds_SALE160.GetColumn(ds_SALE160.currow,"DAT03");
		var aef03=ds_SALE160.GetColumn(ds_SALE160.currow,"AEF03");
		var per03=parseInt(ds_SALE160.GetColumn(ds_SALE160.currow,"PER03"));

		if(length(makcr)!=0 && length(imfdt)!=0 && imfdt!=0){
			if(length(upddt)==0 || upddt=0){
				alert(setPopMessage("4737"));
				setMessage("4737");
				return false;
			}
			if(length(clhct)==0){
				alert(setPopMessage("4739"));//[알림]출하횟수를 입력하세요.
				setMessage("4739");
				return false;			
			}
			else{
				if(clhct==1){
					if(length(dat01)==0 || dat01==0 || length(aef01)==0){
						alert(setPopMessage("4736"));
						setMessage("4736");
						return false;
					}
					if(per01 <1 || per01 > 100){
						alert(setPopMessage("4738"));
						setMessage("4738");		
						return false;
					}
				}
				else if(clhct==2){
					if(length(dat01)==0 || dat01==0 || length(aef01)==0 ||
					 length(dat02)==0 || dat02==0 || length(aef02)==0){
						alert(setPopMessage("4736"));
						setMessage("4736");
						return false;
					}
					if(per01 <1 || per01 >100 || per02 <1 || per02 >100){
						alert(setPopMessage("4738"));
						setMessage("4738");			
						return false;
					}
				}
				else if(clhct==3){
					if(length(dat01)==0 || dat01==0 || length(aef01)==0 ||
					 length(dat02)==0 || dat02==0 || length(aef02)==0 || 
					 length(dat03)==0 || dat03==0 || length(aef03)==0){
						alert(setPopMessage("4736"));
						setMessage("4736");
						return false;
					}
					if(per01 <1 || per01 >100 || per02 <1 || per02 >100 || per03 <1 || per03 >100){
						alert(setPopMessage("4738"));
						setMessage("4738");			
						return false;
					}
				}	
			}
		}
		return true;			
	}		
}
