function getSALE200(){

	if(!validationChk()){
		return;
	}	

	var orgcomps="";
	if(ComboBoxPlant.Value=="E1"){
		orgcomps ="PE";
	}
	else{
		orgcomps ="PS";
	}
	var sKind		= "getHOUSE";		
	var sMethodName = "service::pscomlib/selectMST040PF_HOUSE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF_HOUSE=ds_MST040PF_HOUSE";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " GCOMPS=" + quote(orgcomps); //회사(PE/PS)
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	//if (ds_MST040PF_HOUSE.rowcount > 0){
	var arrHouse = "('";
	for(i=0; i< ds_MST040PF_HOUSE.rowcount; i++){
		if(i == ds_MST040PF_HOUSE.rowcount-1){
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "')";
		}else{
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "','";
		}
	}
	tflag="sel";	
	var sKind		= "getSALE200";						
	var sMethodName = "service::pssallib/selectSALE200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE200=ds_SALE200";    						// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); 	//사업부
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); 			//업체
	sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); 			//업체
	sArgument += "@HOUSE=" + quote(arrHouse); 			//업체
	sArgument += "@TFLAG=" + quote(tflag); 			//flag(조회와 추가구분)
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

//등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setSALE200(){

	if(!validationChk()){
		return;
	}

	if(length(st_MKHAD.Text)==0){
		st_MKHAD.Text=0;
	}
	if(length(st_GIVQY.Text)==0){
		st_GIVQY.Text=0;
	}
	if(length(st_REQTY.Text)==0){
		st_REQTY.Text=0;
	}
	var sKind		= "setSALE200";						
	var sMethodName = "service::pssallib/saveSALE200.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SALE200=ds_SALE200:U";        //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE200_CHECK=ds_SALE200_CHECK";  		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);	
		sArgument += " TFLAG=" + quote(tflag);	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	tflag="sel";
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SALE200_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_SALE200_CHECK.GetRowCount();i++){
			alert("품번: " +ds_SALE200_CHECK.GetColumn(i,"PSPNO") 
			+ " = " + ds_SALE200_CHECK.GetColumn(i,"RTMSG"));
		}
		getSALE200();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var pyvndVal = st_PYVND.Text;
	var pspnoVal = st_PSPNO.Text;
	ed_PYVND.BKColor="white";
	ed_PSPNO.BKColor="white";

	if (pyvndVal == "" )
	{
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("4674"));//[알림]업체를 입력하세요.
		setMessage("4674");	
		result = false;
	}
	else if (pspnoVal == "" )
	{
		ed_PSPNO.BKColor="paleturquoise";
		alert(setPopMessage("2017"));//[알림] 품번을 입력하세요.
		setMessage("2017");	
		result = false;
	}	
	return result;
}

