function setExcel()
{
	var fName   = '\\' + "업체재고마스타 등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|업체재고마스타 등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


