function fn_Input()
{
	if(!validationChk()){
		return;
	}
	http.Sync 	= true;
	var sKind		= "getMKPNO";		
	var sMethodName = "service::pscomlib/selectEM000PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_EM000PR=ds_EM000PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text); //업체
		sArgument  += " PRTNO=" + quote(ed_PSPNO.Text); //품번
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_EM000PR.GetRowCount() < 1){
		//alert("Part Number Cross Ref MA에 미등록된 득의선,품번입니다.");
		alert(setPopMessage("4001"));
		setMessage("4001");
		return false;
	}
	var orgcomps="";
	if(ComboBoxPlant.Value=="E1"){
		orgcomps ="PE";
	}
	else{
		orgcomps ="PS";
	}
	var sKind		= "getHOUSE";		
	var sMethodName = "service::pscomlib/selectMST040PF_HOUSE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF_HOUSE=ds_MST040PF_HOUSE";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " GCOMPS=" + quote(orgcomps); //회사(PE/PS)
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	//if (ds_MST040PF_HOUSE.rowcount > 0){
	var arrHouse = "('";
	for(i=0; i< ds_MST040PF_HOUSE.rowcount; i++){
		if(i == ds_MST040PF_HOUSE.rowcount-1){
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "')";
		}else{
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "','";
		}
	}


	tflag="sel";					
	var sKind		= "getSALE200";						
	var sMethodName = "service::pssallib/selectSALE200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE200=ds_SALE200";    						// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); 	//사업부
	sArgument += " PYVND=" + quote(ed_PYVND.Text); 			//업체
	sArgument += " PSPNO=" + quote(ed_PSPNO.Text); 			//업체
	sArgument += " HOUSE=" + quote(arrHouse); 			//업체
	sArgument += " TFLAG=" + quote(tflag); 	
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if (ds_SALE200.GetRowCount() > 0){
		//alert("[경고] 이미 등록된 자료입니다. 조회 후 수정하시기 바랍니다.");
		ds_SALE200.ClearData();
		alert(setPopMessage("4022"));//[경고] 이미 등록된 자료입니다. 조회 후 수정하시기 바랍니다.
		setMessage("4022");
		return false;
	}
	tflag="add";					
	var sKind		= "getSALE200";						
	var sMethodName = "service::pssallib/selectSALE200.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE200=ds_SALE200";    						// 조회 후 돌려 받을 DataSet 명칭
