var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.3"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); 				//공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSALE201(){

	if(!validationChk()){
		return;
	}

	var orgcomps="";
	if(ComboBoxPlant.Value=="E1"){
		orgcomps ="PE";
	}
	else{
		orgcomps ="PS";
	}
	var sKind		= "getHOUSE";		
	var sMethodName = "service::pscomlib/selectMST040PF_HOUSE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF_HOUSE=ds_MST040PF_HOUSE";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " GCOMPS=" + quote(orgcomps); //회사(PE/PS)
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	//if (ds_MST040PF_HOUSE.rowcount > 0){
	var arrHouse = "('";
	for(i=0; i< ds_MST040PF_HOUSE.rowcount; i++){
		if(i == ds_MST040PF_HOUSE.rowcount-1){
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "')";
		}else{
			arrHouse = arrHouse+ds_MST040PF_HOUSE.GetColumn(i,"MAJCD_H") + "','";
		}
	}
	var orderVal="";
	if(rdoggub.Value=="1"){
		orderVal="ORDER BY B.CARCD, A.PSPNO";
	}
	else{
		orderVal="ORDER BY A.COMPS, A.PYVND, A.PSPNO";

	}
	var sKind		= "getSALE201";						
	var sMethodName = "service::pssallib/selectSALE201.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             					// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE201=ds_SALE201";    						// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); //품번
	sArgument += "@HOUSE=" + quote(arrHouse);	//창고
	sArgument += "@ORDERVAL=" + quote(orderVal);	//창고
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setSALE201(){

	if(length(trim(st_PYVND.Text)) < 1){
		alert(setPopMessage("2516"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("2516");	
		ed_PYVND.SetFocus();
		return;
	}

	var sKind		= "setSALE201";						
	var sMethodName = "service::pssallib/saveSALE201.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SALE201=ds_SALE201:U";               	//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE201_CHECK=ds_SALE201_CHECK";  		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SALE201_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_SALE201_CHECK.GetRowCount();i++){
			alert("품번: " +ds_SALE201_CHECK.GetColumn(i,"PSPNO") 
			+ " = " + ds_SALE201_CHECK.GetColumn(i,"RTMSG"));
		}
		getSALE201();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}	
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var pyvndVal = st_PYVND.Text;
	ed_PYVND.BKColor="white";

	if (pyvndVal == "" )
	{
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("2516"));//[경고] 필수입력항목입니다. (업체번호)
		setMessage("2516");	
		result = false;
	}
	return result;
}

