}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = '\\' + "업체재고마스타(차종별) 등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|업체재고마스타(차종별) 등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

