function setSALE210C(){
	if(length(trim(Div0.cb_searchCodeName1.Value)) < 1){
		alert(setPopMessage("1006"));  //[알림] 지원하지 않는 시스템 입니다. 정보관리팀으로 문의하십시요.
		setMessage("1006");	
		return;
	}

	if(length(trim(Div0.ed_pyvnd.Text)) != 4){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		Div0.ed_pyvnd.SetFocus();
		return;
	}

	if(length(trim(Div0.st_PYVND.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		Div0.ed_pyvnd.SetFocus();
		return;
	}

	if(length(trim(Div0.ed_TRKNO1.Text)) < 1){
		alert(setPopMessage("4717"));  //[확인] 트럭번호를 확인하세요..
		setMessage("4717");	
		Div0.ed_TRKNO1.SetFocus();
		return;
	}

	if(confirm("업체재고운송량생성 작업입니다. 작업하시겠습니까? ") == false) return;

	/* 삭제 : 2019.11.28 NEWDIONE로 SSO관리전환
	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	*/
	//callUser = ds_ssoaccount.GetColumn(0,"SYSTEM_ID");
	//callPass = ds_ssoaccount.GetColumn(0,"SYSTEM_PW");

	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}

	var t1 = right("       "+Div0.ed_TRKNO1.Text,7);
	var t2 = right("       "+Div0.ed_TRKNO2.Text,7);
	var t3 = right("       "+Div0.ed_TRKNO3.Text,7);
	var t4 = right("       "+Div0.ed_TRKNO4.Text,7);
	var t5 = right("       "+Div0.ed_TRKNO5.Text,7);
	var t6 = right("       "+Div0.ed_TRKNO6.Text,7);
	var t7 = right("       "+Div0.ed_TRKNO7.Text,7);
	var t8 = right("       "+Div0.ed_TRKNO8.Text,7);
	var t9 = right("       "+Div0.ed_TRKNO9.Text,7);
	var t10 = right("       "+Div0.ed_TRKNO10.Text,7);
	var t11 = right("       "+Div0.ed_TRKNO11.Text,7);
	var t12 = right("       "+Div0.ed_TRKNO12.Text,7);
	var t13 = right("       "+Div0.ed_TRKNO13.Text,7);
	var t14 = right("       "+Div0.ed_TRKNO14.Text,7);
	var t15 = right("       "+Div0.ed_TRKNO15.Text,7);

	//리모트콜 실행(SALE210RC용)
	param = Div0.cb_searchCodeName1.Value + "0000" + Div0.ed_pyvnd.Text+t1+t2+t3+t4+t5+t6+t7+t8+t9+t10+t11+t12+t13+t14+t15;
	for(i=length(param)+1;i<=30;i++){
		param += " ";
	}
	param += callUser;	
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	st_Msg.Visible = true;
	Div0.Enable = false;
	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			st_Msg.Visible = false;
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),125,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				//Div0.Enable = true;
				alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				setMessage("1001");	
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),125,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),130,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),140,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				//alert(setPopMessage("1008"));	// 현재 시스템에 락이 걸렸습니다. 전산관리자에게 문의하세요.
				//setMessage("1008");
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),125,3) == "771") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("4717")); //[[확인] 트럭번호를 확인하세요..
				setMessage("4717");	
				return false;
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),125,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				Div0.Enable = true;
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else{
		alert(rtnCode);
		if (rtnCode == 0){
			alert(setPopMessage("1001"));
			setMessage("1001");
		}else if (rtnCode == 1){
			alert("타사용자가 사용중입니다. 확인바랍니다.");
			//setMessage("1002");
		}else{
			alert(setPopMessage("1002"));
			setMessage("1002");
		}
	}
}

