}

function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectMST040PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";
	var sArgument   = "";
		sArgument  += " LIB2=" + quote(Lib2);
		sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument  += " MAJCD=" + quote("S"+lpad(ed_PYVND.Text,0,4));
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST040PF.GetRowCount() < 1){
		alert(setPopMessage("1502"));
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_MST040PF.GetColumn(0,"MAJMK");
	http.usewaitcursor = true;	
}

function imgMaker_OnClick(obj,nX,nY)
{
	var scomp =ComboBoxPlant.Value;
	var sTemp=trim(ed_PYVND.Text);
	if (length(trim(ed_PYVND.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "E0";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp+ " " + "COMP="+scomp,584,528);
	if (length(rtn) != 0){
		ed_PYVND.Text = rtn[1];
		st_PYVND.Text = rtn[2];
	}
}

