function getOGMPrtno(tVal)
{
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectBM008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BM008PR=ds_BM008PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " ITNBR=" + quote(tVal);
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_BM008PR.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}	
}

function ds_SALZ500_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="IORDT"){
		if(varNewValue != "D" && varNewValue != "E" && varNewValue != "K"){
			alert("수주유형은 D/E만 입력가능합니다.");
			return false;
		}
	}

	if( strColumnID == "PSPNO" ){
		if(varNewValue != "" ){
			var tval=getOGMPrtno(varNewValue);
			if(tval=="0"){
				alert("ITEM MA에 미등록된 품번입니다.");
				return false;
			}

		}
	}

	if(strColumnID=="IALYR"){
		if(parseInt(varNewValue) > 100 || parseInt(varNewValue) < 1 ){
			alert("적용율은 100이하만 입력가능합니다.");
			return false;
		}
	}

	if(strColumnID=="IDISR"){
		if(parseInt(varNewValue) > 100 || parseInt(varNewValue) < 1 ){
			alert("배분율은 100이하만 입력가능합니다.");
			return false;
		}
	}

	if(strColumnID=="IQTYP"){
		if(varNewValue == 0 || varNewValue == ""){
			alert("단위수량은 1부터 입력가능합니다.");
			return false;
		}
	}
}

function ds_SALZ500_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "PSPNO" ){
		if(varNewValue != "" ){
			var tval=getOGMPrtno(varNewValue);
				if(tval!="0"){
				ds_SALZ500.SetColumn(nRow,"ITDSC",ds_BM008PR.GetColumn(0,"ITDSC"));
			}	
		}
	}	
}

function ds_SALZ500_OnRowPosChanged(obj,nOldRow,nRow)
{
	if(ds_SALZ500.GetRowType(ds_SALZ500.currow)=="insert" ){
		MainGrid.SetCellProp("body",0,"Edit","upperchar");
		MainGrid.SetCellProp("body",1,"Edit","normal");

	}else{
		//MainGrid.SetColProp(7,"Edit(e)","number");
		MainGrid.SetCellProp("body",0,"Edit","none");
		MainGrid.SetCellProp("body",1,"Edit","none");
	}	
}

function ds_SALZ500_CanRowPosChange(obj,nOldRow,nRow)
{
	if(ds_SALZ500.GetRowType(ds_SALZ500.currow)=="update" ||ds_SALZ500.GetRowType(ds_SALZ500.currow)=="insert" ){

		var pspno=ds_SALZ500.GetColumn(ds_SALZ500.currow,"PSPNO");
		var iordt=ds_SALZ500.GetColumn(ds_SALZ500.currow,"IORDT");
		var ialyr=ds_SALZ500.GetColumn(ds_SALZ500.currow,"IALYR");
		var idisr=ds_SALZ500.GetColumn(ds_SALZ500.currow,"IDISR");
		var iqtyp=ds_SALZ500.GetColumn(ds_SALZ500.currow,"IQTYP");
		var frdt=ds_SALZ500.GetColumn(ds_SALZ500.currow,"IFRDT");
		var todt=ds_SALZ500.GetColumn(ds_SALZ500.currow,"ITODT");
		if(length(pspno)!=0){
			if(length(iordt)==0){
				alert("유형을 입력하세요.");
				return false;
			}
			if(length(ialyr)==0){
				alert("적용율을 입력하세요.");
				return false;
			}
			if(length(idisr)==0){
				alert("배분율을 입력하세요.");
				return false;
			}
			if(length(iqtyp)==0){
				alert("단위수량을 입력하세요.");
				return false;
			}
			if(frdt > todt){
				alert("시작일자와 종료일자를 확인하세요.");
				return false;
			}												
		}	
	}
}

function ed_SHPTO_OnChanged(obj,strText)
{
	ds_SALZ500.ClearData();
}
