function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	rdoggub.Value=1;
	MainGrid.UserData = MainGrid.Height;
	getSYSDATE();
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSYSDATE(){
	var sKind		= "getSYSDATE";						
	var sMethodName = "service::pscomlib/selectSYSDATE.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SYSDATE=ds_SYSDATE";
	var sArgument   = "";
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;	
}
function getMst040()
{
	var sKind		= "getMST040PF";						
	var sMethodName = "service::pscomlib/selectMST040.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST040PF=ds_MST040PF";   					// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " MAJCD=" + quote("DANJP");
	http.Sync= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	st_TRKNO.Text = "이전전표 번호 : "+ds_MST040PF.GetColumn(0,"MJINT");
	http.Sync= false;
}

function getSCDZ105(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getSCDZ105";						
	var sMethodName = "service::pssallib/selectSCDZ105.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SCDZ105=ds_SCDZ105";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); 	//사업부
	sArgument += "@TRKNO=" + quote(ed_TRKNO.Text); //전표번호
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		getMst040();
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setSCDZ105(){

	if(ds_SCDZ105.GetRowType(ds_SCDZ105.currow)=="update" ||ds_SCDZ105.GetRowType(ds_SCDZ105.currow)=="insert" ){
		if(!ds_SCDZ105_CanRowPosChange()){
			return;
		}
	} 	

	var sKind		= "setSCDZ105";						
	var sMethodName = "service::pssallib/saveSCDZ105.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SCDZ105=ds_SCDZ105:U";             //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SCDZ105_CHK=ds_SCDZ105_CHK";       // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); 	//사업부
		sArgument += " MAJCD=" + quote("DANJP");
		sArgument += " MJINT=" + quote(ed_TRKNO.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if (ds_SCDZ105_CHK.GetRowCount() > 0){
		for(var i=0;i<ds_SCDZ105_CHK.GetRowCount();i++){
			alert("품번 : " +ds_SCDZ105_CHK.GetColumn(i,"PSPNO") + " = " + ds_SCDZ105_CHK.GetColumn(i,"RTMSG"));
		}
		getSCDZ105();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}	
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var trknoVal = ed_TRKNO.Text;
		ed_TRKNO.BKColor="white";

	if (length(trknoVal) ==0)
	{
		ed_TRKNO.BKColor="paleturquoise";
		alert(setPopMessage("4012"));//[확인] 전표번호를 입력바랍니다.
		setMessage("4012");	
		result = false;
	}
	return result;
}

