function fn_Input()
{

	if(rdoggub.Value==1 && length(st_TRKNO.Text)==0){
		//alert("[알림]조회후 사용이 가능합니다.");
		alert(setPopMessage("4735"));//[알림]조회후 사용이 가능합니다.
		setMessage("4735");
		return;
	}

	if(length(ed_TRKNO.Text)==0){
		alert(setPopMessage("4012"));//[확인] 전표번호를 입력바랍니다.
		setMessage("4012");
		return;
	}
	if(rdoggub.Value==1){
		ed_PYVND.Text=ds_SCDZ105.GetColumn(0,"PYVND");
		st_PYVND.Text=ds_SCDZ105.GetColumn(0,"CUSNM");
	}
	if(length(st_PYVND.Text)==0){
		alert(setPopMessage("4671"));//[확인] 업체 확인바랍니다.
		setMessage("4671");		
		return;
	}
	if(checkInsertCount("ds_SCDZ105")== false) return;
	//if(ds_SCDZ105.rowcount < 1) fn_Search();  
		ds_SCDZ105.AddRow();
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"COMPS",ComboBoxPlant.Value);
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"TRKNO",ed_TRKNO.Text);
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"PYVND",ed_PYVND.Text);
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"CUSNM",st_PYVND.Text);
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"SHPTO",0);
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"JPSDT",cmb_JPSDT.Value);
		ds_SCDZ105.SetColumn(ds_SCDZ105.currow,"DCRDT",ds_SYSDATE.GetColumn(0,"DT2"));

	}
}

function fn_Delete()
{
	ds_SCDZ105.DeleteRow(ds_SCDZ105.currow);
	alert(setPopMessage("1011"));     //[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.
	setMessage("1011");	
}

