function ds_SCDZ105_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
/*
	if( strColumnID == "PYVND"){
	//업체입력시 업체명 가져옴.
		if(varNewValue != "" ){
			ds_SCDZ105.SetColumn(nRow,"CUSNM",trim(ds_CUSMAS1.GetColumn(0,"CUSNM")));
		}
		if(varNewValue == "" ){	
			ds_SCDZ105.SetColumn(nRow,"CUSNM", "");
		}
	}
*/	
	if( strColumnID == "PSPNO"){
	//품번입력시 업체품번 가져옴.
		if(varNewValue != "" ){
			//ds_SCDZ105.SetColumn(nRow,"PSPNO",trim(ds_EM000PR.GetColumn(0,"CPRTN")));
			ds_SCDZ105.SetColumn(nRow,"PSPNO",toUpper(varNewValue));
			ds_SCDZ105.SetColumn(nRow,"MKPNO",trim(ds_EM000PR.GetColumn(0,"CPRTN")));
			ds_SCDZ105.SetColumn(nRow,"CARCD",trim(ds_MST100PF.GetColumn(0,"CARCD")));
			ds_SCDZ105.SetColumn(nRow,"BDYCD",trim(ds_MST100PF.GetColumn(0,"BDYCD")));
		}
		if(varNewValue == "" ){	
			ds_SCDZ105.SetColumn(nRow,"MKPNO", "");
			ds_SCDZ105.SetColumn(nRow,"CARCD","");
			ds_SCDZ105.SetColumn(nRow,"BDYCD","");
		}
	}
	if( strColumnID == "JPSAM"){
		if(varNewValue == "" ){	
			ds_SCDZ105.SetColumn(nRow,"JPSAM", 0);
		}
	}	
}

function ds_SCDZ105_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_SCDZ105.GetRowType(ds_SCDZ105.currow)=="update" ||ds_SCDZ105.GetRowType(ds_SCDZ105.currow)=="insert" ){
		var pspno=ds_SCDZ105.GetColumn(ds_SCDZ105.currow,"PSPNO");
		var reson=ds_SCDZ105.GetColumn(ds_SCDZ105.currow,"RESON");
		var jpsam=ds_SCDZ105.GetColumn(ds_SCDZ105.currow,"JPSAM");

		var duCHK=0;
		for(i=0; i<ds_SCDZ105.GetRowCount(); i++){
			if(pspno==ds_SCDZ105.GetColumn(i,"PSPNO")){
				duCHK++;
			}
		}

		if(length(pspno)!=0){
			if(length(pspno)!=0 && duCHK>=2){
				//alert("[경고]동일한 품번이 등록되어 있습니다.");
				alert(setPopMessage("4681"));//[경고]동일한 품번이 등록되어 있습니다.
				setMessage("4681");
				result=false;			
			}			
			else if(length(reson)==0){
				//alert("[알림]이유코드를 입력하세요.");
				alert(setPopMessage("4678"));//[알림]이유코드를 입력하세요.
				setMessage("4678");
				//MainGrid.SetCellPos(4);
				//MainGrid.SetFocus();				
				result=false;
			}
			else if(length(jpsam)==0){
				//alert("[확인] 금액을 확인바랍니다.");
				alert(setPopMessage("4052"));//[확인] 금액을 확인바랍니다.
				setMessage("4052");
				//MainGrid.SetCellPos(5);
				//MainGrid.SetFocus();	
				result=false;			
			}				
			else if(length(reson)!=0){
				if(reson=="87" && jpsam <0){
					//alert("[알림]이유 87은 단가정리 (+)만 입력가능합니다.");
					alert(setPopMessage("4679"));//[알림]이유코드를 입력하세요.
					setMessage("4679");
					//MainGrid.SetCellPos(5);
					//MainGrid.SetFocus();	
					result=false;

				}
				if(reson=="98" && jpsam >0){
					//alert("[알림]이유 98은 단가정리 (-)만 입력가능합니다.");
					alert(setPopMessage("4680"));//[알림]이유코드를 입력하세요.
					setMessage("4680");
					//MainGrid.SetCellPos(5);
					//MainGrid.SetFocus();	
					result=false;
				}
			}		
		}
	return result;		
	}
}

function ed_PYVND_OnCharChanged(obj,strPreText,strPostText)
{
	st_PYVND.Text = "";
}

function ed_PYVND_OnChanged(obj,strText)
{
	ds_SCDZ105.ClearData();
}

function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("1502"));
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;	
}

function rdoggub_OnClick(obj)
{
	if(rdoggub.Value==1){
		ed_TRKNO.Enable=true;
		ed_PYVND.Enable=false;
		imgMaker.Enable=false;
		cmb_JPSDT.Enable=false;
		ed_TRKNO.Text="";
		st_TRKNO.Text="";
		ed_PYVND.Text="";
		st_PYVND.Text="";
		cmb_JPSDT.Value="";
		//Button4.Enable=false;
	}
	if(rdoggub.Value==2){
		ed_TRKNO.Enable=false;
		ed_PYVND.Enable=true;
		imgMaker.Enable=true;
		cmb_JPSDT.Enable=true;
		cmb_JPSDT.Value=ds_SYSDATE.GetColumn(0,"DT2");
		ed_TRKNO.Text="";
		st_TRKNO.Text="";
		getMst040();
		ed_TRKNO.Text=parseInt(ds_MST040PF.GetColumn(0,"MJINT"))+1;
		//Button4.Enable=true;
		cmb_JPSDT_OnKillFocus();
	}
}

function cmb_JPSDT_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//전표일자 CHECK
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getMST090PF";		
	var sMethodName = "service::pscomlib/selectMST090PF.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MST090PF=ds_MST090PF";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PRCDT=" + quote(cmb_JPSDT.Value); //전표일자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_MST090PF.GetRowCount() < 1){
		//alert("[확인]전표일자를 확인바랍니다.");
		alert(setPopMessage("4027"));
		setMessage("4027");
		cmb_JPSDT.SetFocus();
		return;
	}
	http.usewaitcursor = true;
}

function cmb_JPSDT_OnChanged(obj,strOldText,strNewText)
{
	ds_SCDZ105.ClearData();
}
