var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	//MainGrid.UserData = MainGrid.Height;
	//ComboBoxPlant.Value="C1";
	//ed_PYVND1.Text="1100";
	//ed_PYVND2.Text="1100";
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	ds_ComCode_PLNT.AddRow();
	ds_ComCode_PLNT.SetColumn(ds_ComCode_PLNT.lastrow,"CLASS","PLANT");
	ds_ComCode_PLNT.SetColumn(ds_ComCode_PLNT.lastrow,"CODE1","PS");
	ds_ComCode_PLNT.SetColumn(ds_ComCode_PLNT.lastrow,"CODE2","");
	ds_ComCode_PLNT.SetColumn(ds_ComCode_PLNT.lastrow,"CODE_NAME","전체");
	//ComboBoxPlant.Enable = false; 
}
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSJSC103Q(){

	if(!validationChk()){
		return;
	}
	var sPYVND1="";
	var sPYVND2="";
	var sPSPNO1="";
	var sPSPNO2="";
	//업체
	if(length(ed_PYVND1.Text)==0){
		sPYVND1="00000000";
	}
	else{
		sPYVND1=ed_PYVND1.Text;
	}	
	if(length(ed_PYVND2.Text)==0){
		sPYVND2="99999999";
	}
	else{
		sPYVND2=ed_PYVND2.Text;
	}
	//품번
	if(length(ed_PSPNO1.Text)==0){
		sPSPNO1=" ";
	}
	else{
		sPSPNO1=ed_PSPNO1.Text;
	}	
	if(length(ed_PSPNO2.Text)==0){
		sPSPNO2="999999999999999";
	}
	else{
		sPSPNO2=ed_PSPNO2.Text;
	}

	//회계년도
	var sChkym=substr(cmb_SDATE.Value,0,4);
	if(substr(cmb_SDATE.Value,4,2) <= '03'){
		sChkym = parseInt(sChkym)-1;
	}
	var sKind		= "getSJSC103Q";						
	var sMethodName = "service::pssallib/selectSJSC103Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SJSC103Q=ds_SJSC103Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@SDATE=" + quote(substr(cmb_SDATE.Value,0,6)); //적송년월
	sArgument += "@JSOYY=" + quote(sChkym); //적송년(적송년월FR의 년도만보냄)
	sArgument += "@JSOMM=" + quote(substr(cmb_SDATE.Value,4,2)); //적송자료 조회 조건
	sArgument += "@PYVND1=" + quote(sPYVND1); //업체
	sArgument += "@PYVND2=" + quote(sPYVND2); //업체
	sArgument += "@PSPNO1=" + quote(sPSPNO1); //품번
	sArgument += "@PSPNO2=" + quote(sPSPNO2); //품번

	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var sdVal = substr(cmb_SDATE.Value,0,6);
	cmb_SDATE.BKColor="white";

	if(cmb_SDATE.Value=""){
		cmb_SDATE.BKColor="paleturquoise";
		//alert(setPopMessage("4060"));
		alert("[확인] 적송년월을 확인바랍니다.");
		result =false;
	}

	return result;
}

