/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "품번별 적송현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|품번별 적송현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

