function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");
	Div1.cb_searchCodeName1.Value = comps;

}

/********************************************************************** 
 * 내용설명 : Excel FILE 가져오기
 * 주요변수 : 
 **********************************************************************/ 
function Div1_Button0_OnClick(obj)
{

	if(length(trim(Div1.ed_pyvnd.Text)) != 4){
		alert(setPopMessage("1202")); 
		setMessage("1202");	
		Div1.ed_pyvnd.SetFocus();
		return;
	}
	if(getHOUSE() != true){
		alert(setPopMessage("4033"));//[확인]등록된 창고가 없습니다. 업체를 확인해주세요.
		setMessage("4033");
		Div1.ed_pyvnd.SetFocus();
		return false;
	}
	if(length(trim(Div1.ed_taxno.Text)) < 1){
		alert(setPopMessage("4723")); 
		setMessage("4723");	
		Div1.ed_taxno.SetFocus();
		return;
	}
	if(length(trim(Div1.cmb_TAXDT.Value)) != 8){
		alert(setPopMessage("4724"));//[확인] 계산서일자를 확인바랍니다.
		setMessage("4724");	
		Div1.ed_taxno.SetFocus();
		return;
	}
	FileDialog0.Filter = "ALL File(.xls)|*.xls";	
	FileDialog0.Type = "OPEN";
	if (!FileDialog0.Open()) {
		alert(setPopMessage("1009"));	//  파일 선택 창을 여는데 실패하였습니다.
		return;
	}

	ds_SEND.ClearData();
	ds_EM000PR.ClearData();
	ds_EM001PR.ClearData();
	//ds_RECEIVE.ClearData();
	//ds_SJTZ210UC_BOX.ClearData();

	Div2.Visible = true;
	var file_name = FileDialog0.FileName;
	var file_path = FileDialog0.FilePath;
	var path = file_path + "\\" + file_name;
	setMessageDirect(path);	
	Div1.ed_path.Text = path;
	ext_ExcelImportByIndex(path,0,"ds_SEND",1,1,0,2);  

	if (ds_SEND.GetRowCount() < 1){
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}
	ds_SEND.Sort("PSPNO");
	//비교할 정보 가져오기
	check_ALL();

	//excel data 유효성 검사 및 전표마스타dataset생성 
	chkExcelData();

	Div2.Visible = false;
}
/********************************************************************** 
 * 내용설명 : 용차사용신청 생성
 * 주요변수 : 
 **********************************************************************/ 
 function setSJTZ210UC(){
	if (ds_SEND.GetRowCount() < 1) {
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}
	if(length(trim(Div1.ed_pyvnd.Text)) != 4){
		alert(setPopMessage("1202")); 
		setMessage("1202");	
		Div1.ed_pyvnd.SetFocus();
		return;
	}
	if(getHOUSE() != true){
		alert(setPopMessage("4033"));//[확인]등록된 창고가 없습니다. 업체를 확인해주세요.
		setMessage("4033");
		Div1.ed_pyvnd.SetFocus();
		return false;
	}
	if(length(trim(Div1.ed_taxno.Text)) < 1){
		alert(setPopMessage("4723")); 
		setMessage("4723");	
		Div1.ed_taxno.SetFocus();
		return;
	}
	if(length(trim(Div1.cmb_TAXDT.Value)) != 8){
		alert(setPopMessage("4724"));//[확인] 계산서일자를 확인바랍니다.
		setMessage("4724");	
		Div1.ed_taxno.SetFocus();
		return;
	}
	for(var i=0;i<ds_SEND.GetRowCount();i++){
		if (ds_SEND.getcolumn(i,"UPERR") != "TT"){
			alert("[경고] error항목 존재. 생성가능여부를 확인하시고 다시 작업바랍니다."); 
			return;
		}
	}
	if(confirm(Div1.ed_pyvnd.Text+" - "+Div1.ed_taxno.Text+" 기존DATA는 삭제됩니다."+"생성하시겠습니까? ") == false) return;

 	http.Sync = true;
	var sKind		= "SAVE_SJTZ210UC";						
	var sMethodName = "service::pssallib/insertSJTZ210UC.do";
	var sInDataSet  = "ds_SEND=ds_SEND";
	var sOutDataSet = "ds_SJTZ210UC_CHECK=ds_SJTZ210UC_CHECK";
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div1.cb_searchCodeName1.Value);
	sArgument += " PYVND=" + quote(Div1.ed_pyvnd.Value);
	sArgument += " SHPTO=" + quote("0");
	sArgument += " TAXNO=" + quote(Div1.ed_taxno.Value);
	sArgument += " TAXDT=" + quote(Div1.cmb_TAXDT.Value);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

function chkExcelData(){
	//ds_SJTZ210UC_BOX.ClearData();
	for(var z=0;z<ds_SEND.GetRowCount();z++){
		ds_SEND.SetColumn(z,"UPERR","TT");
		var pspno = rtrim(ds_SEND.GetColumn(z,"PSPNO"));
		//var psseq = ds_SEND.GetColumn(z,"PSSEQ");
		//var taxdt = ds_SEND.GetColumn(z,"TAXDT");
		var reson = ds_SEND.GetColumn(z,"RESON");
		var taxqy = toNumber(ds_SEND.GetColumn(z,"TAXQY"));
		var taxcs = toNumber(ds_SEND.GetColumn(z,"TAXCS"));

		var stlwh=ds_EM035PR.GetColumn(0,"STLWH");

		//length check
		if (length(pspno) < 1 || length(pspno) > 15 || length(reson) != 2 || length(trim(taxqy)) < 1 || length(trim(taxcs)) < 1) {
			ds_SEND.SetColumn(z,"UPERR","E1"); //LENGTH 확인
		}
		//숫자check
		if (!numberChk(reson) || !numberChk(taxqy) || !numberChk(taxcs)){ 
			ds_SEND.SetColumn(z,"UPERR","E2"); //숫자CHECK
		}
		//동일품번seq생성
		if(z == 0){
			ds_SEND.SetColumn(z,"PSSEQ","0"); //1행
		}else{
			if(pspno == rtrim(ds_SEND.GetColumn(z-1,"PSPNO"))){
				var psseq = toNumber(ds_SEND.GetColumn(z-1,"PSSEQ"));
				ds_SEND.SetColumn(z,"PSSEQ",psseq+1);
			}else{
				ds_SEND.SetColumn(z,"PSSEQ","0");
			}
		}

		//이유코드check
		if(reson == "80" || reson == "81"){  //[알림]이유코드 80/81은 수량, 단가 (+)만 입력가능합니다.
			if(taxqy <= 0 || taxcs <= 0){
				ds_SEND.SetColumn(z,"UPERR","E3");
			}
		}
		if(reson == "90"){                   //[알림]이유코드 90은 수량(-), 단가 (+)만 입력가능합니다.
			if(taxqy >= 0 || taxcs <= 0){
				ds_SEND.SetColumn(z,"UPERR","E4");
			}
		}
		if(reson == "91"){                   //[알림]이유코드 91은 수량(+), 단가 (-)만 입력가능합니다.
			if(taxqy <= 0 || taxcs >= 0){
				ds_SEND.SetColumn(z,"UPERR","E5");
			}
		}
		if(!(reson == "80" || reson == "81" || reson == "90" || reson == "91")){
			ds_SEND.SetColumn(z,"UPERR","E6"); //RESON CODE ERROR
		}
		//Part Number Cross Ref MA Check
		var row_em000 = ds_EM000PR.FindRow("PRTNO",pspno);
		if (row_em000 == -1){
			ds_SEND.SetColumn(z,"UPERR","E7");
		}else{
			ds_SEND.SetColumn(z,"MKPNO",ds_EM000PR.GetColumn(row_em000,"CPRTN"));
		}
		//단가여부 Check
		var row_em001 = ds_EM001PR.FindRow("PRTNO",pspno);
		if (row_em001 == -1){
			ds_SEND.SetColumn(z,"UPERR","E8");
		}else{
			ds_SEND.SetColumn(z,"CURCD",ds_EM001PR.GetColumn(row_em001,"CURCD"));
		}
		//창고코드삽입
		ds_SEND.SetColumn(z,"STLWH",stlwh);
		//금액산출
		ds_SEND.SetColumn(z,"TAXAM",taxqy*taxcs);
		//순서
		ds_SEND.SetColumn(z,"PYSEQ",z+1);
	}
}
/********************************************************************** 
 * 내용설명 : 시간형식Check
 **********************************************************************/ 
function Time_Check(strText)
{
	var strChk_t = replace(strText,':','');
	var strChk = right("0000"+strChk_t,4);
	if((left(strChk,2) > 24) || (right(strChk,2) > 59)){
		return false;
	}
	return true;
}

