function setExcel()
{
	var fName   = "월검수세금계산서 TX현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월검수세금계산서 TX현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

