var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var nowDate = today();
var rtnCode;
var job_month;

var pgmLib = "*LIBL";
var pgmName = "SJTZ290RC";
var param = "";
var exeTime = 1200;
