}

/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSLTZ100(){

	if(length(trim(ed_LNTNO.Text)) < 1){
		alert(setPopMessage("4004"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("4004");	
		ed_LNTNO.SetFocus();
		return;
	}	
	if(length(trim(ca_INOUT.Value)) < 1){
		alert(setPopMessage("4722"));  //[확인] 입출구분을 확인바랍니다.
		setMessage("4722");	
		ca_INOUT.SetFocus();
		return;
	}
	//조회
	var sKind		= "getSLTZ100";						
	var sMethodName = "service::pssallib/selectSLTZ100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SLTZ100=ds_SLTZ100@ds_SLTZ100_A=ds_SLTZ100_A";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@LNTNO=" + quote(ed_LNTNO.Text);
	sArgument += "@INOUT=" + quote(ca_INOUT.Value);
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		if(ds_SLTZ100_A.GetRowCount() > 0){
			var arrValue = array();
			for(var i=0; i<ds_SLTZ100_A.GetRowCount();i++){
				arrValue[i] = ds_SLTZ100_A.GetColumn(i,"JPSNO");
			}
			MainGrid.ed_JPSNO1.Text = arrValue[0];
			MainGrid.ed_JPSNO2.Text = arrValue[1];
			MainGrid.ed_JPSNO3.Text = arrValue[2];
			MainGrid.ed_JPSNO4.Text = arrValue[3];
			MainGrid.ed_JPSNO5.Text = arrValue[4];
			MainGrid.ed_JPSNO6.Text = arrValue[5];
			change_JPSNO = "F";
		}
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //   [알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	ed_LNTNO.SetFocus();
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSLTZ100(){
	if(ds_SLTZ100.GetRowType(ds_SLTZ100.currow)=="update"){
		if (MainGrid.MaskEdit20.Text == "*"){
			alert(setPopMessage("4005"));  //  [확인] 용차사용신청확인 마감작업이 완료되어 수정 불가능합니다.
			setMessage("4005");
			return;
		}
	}
	if(length(trim(ca_INOUT.Value)) < 1){
		alert(setPopMessage("4722"));  //[확인] 입출구분을 확인바랍니다.
		setMessage("4722");	
		ca_INOUT.SetFocus();
		return;
	}
	if(ds_SLTZ100.GetRowType(ds_SLTZ100.currow)=="insert" || ds_SLTZ100.GetRowType(ds_SLTZ100.currow)=="update" || change_JPSNO=="T"){
		if(length(trim(MainGrid.ca_USEDT.Value)) < 1){ //사용일자
			alert(setPopMessage("4006"));  // [확인] 사용일자를 확인바랍니다.
			setMessage("4006");	
			MainGrid.ca_USEDT.SetFocus();
			return;
		}
		if(length(trim(MainGrid.st_pyvnd.Text)) < 1){ //납품업체
			alert(setPopMessage("4007"));  // [확인] 납품업체를 확인바랍니다.
			setMessage("4007");	
			MainGrid.ed_pyvnd.SetFocus();
			return;
		}
		if(length(trim(MainGrid.ed_MKLOC.Text)) < 1){ //납품장소
			alert(setPopMessage("4008"));  // [확인] 납품장소를 확인바랍니다.
			setMessage("4008");	
			MainGrid.ed_MKLOC.SetFocus();
			return;
		}
		if(length(trim(MainGrid.ed_CARSQ.Text)) < 1){ //출발장소
			if(comps != "E1"){
				alert(setPopMessage("4009"));  // [확인] 출발장소를 확인바랍니다.
				setMessage("4009");	
				MainGrid.ed_CARSQ.SetFocus();
				return;
			}
		}
		if(MainGrid.med_CARTN.Value == 0){ //차량규격
			alert(setPopMessage("4010"));  //  [확인] 차량규격을 확인바랍니다.
			setMessage("4010");	
			MainGrid.med_CARTN.SetFocus();
			return;
		}
		if(length(trim(MainGrid.med_CARTN.Text)) < 1){ //차량규격
			alert(setPopMessage("4010"));  //  [확인] 차량규격을 확인바랍니다.
			setMessage("4010");	
			MainGrid.med_CARTN.SetFocus();
			return;
		}
		if(length(trim(MainGrid.ed_USEGB.Text)) < 1){ //운행구분
			alert(setPopMessage("4011"));  // [확인] 운행구분을 확인바랍니다.
			setMessage("4011");	
			MainGrid.ed_USEGB.SetFocus();
			return;
		}
		if(length(trim(MainGrid.ca_ARVDT.Value)) < 1){ //도착일자 uu20150115
			alert(setPopMessage("4006"));  // [확인] 도착일자를 확인바랍니다.
			setMessage("4006");	
			MainGrid.ca_ARVDT.SetFocus();
			return;
		}
		if(length(trim(MainGrid.med_ARVTM.Value)) < 1){  //도착시간
			ds_SLTZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			MainGrid.med_ARVTM.Value = 0;
			ds_SLTZ100.FireEvent=true; 
		}
		if(length(trim(MainGrid.ed_JPSNO1.Text)) < 1){ //전표번호
			alert(setPopMessage("4012"));  // [확인] 전표번호를 입력바랍니다.
			setMessage("4012");	
			MainGrid.ed_JPSNO1.SetFocus();
			return;
		}
		if(MainGrid.ca_EXPCD.Value == "N"){
			ds_SLTZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			MainGrid.med_USEAM.Text = 0;
			ds_SLTZ100.FireEvent=true;
		}
		if(length(trim(MainGrid.med_USEAM.Text)) < 1 || MainGrid.ca_EXPCD.Value == "N"){
			ds_SLTZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			MainGrid.med_USEAM.Text = 0;
			ds_SLTZ100.FireEvent=true; 
		}
		if(MainGrid.ca_EXPCD.Value == "Y" && MainGrid.med_USEAM.Value == 0){  //
			alert(setPopMessage("4013"));  //[확인] 거래처코드 확인바랍니다.
			setMessage("4013");	
			MainGrid.med_USEAM.SetFocus();
			return;
		}
		if(length(trim(MainGrid.med_DRVPN.Text)) < 1){ //운수업체
			ds_SLTZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
			if (comps == "E1"){
				MainGrid.med_DRVPN.Text = 8810;
			}else{
				MainGrid.med_DRVPN.Text = 0;
			}
			ds_SLTZ100.FireEvent=true; 
		}
		var JPSNO_Count ;
		JPSNO_Count= iif(length(trim(MainGrid.ed_JPSNO1.Text)) == 0,0,1)+
		                  iif(length(trim(MainGrid.ed_JPSNO2.Text)) == 0,0,1)+
		                  iif(length(trim(MainGrid.ed_JPSNO3.Text)) == 0,0,1)+
		                  iif(length(trim(MainGrid.ed_JPSNO4.Text)) == 0,0,1)+
		                  iif(length(trim(MainGrid.ed_JPSNO5.Text)) == 0,0,1)+
		                  iif(length(trim(MainGrid.ed_JPSNO6.Text)) == 0,0,1);
		if(JPSNO_Count == 0){
			alert(setPopMessage("4012"));  // [확인] 전표번호를 입력바랍니다.
			setMessage("4012");	
			MainGrid.ed_MKLOC.SetFocus();
			return;
		}

		ds_SLTZ100_BOX.ClearData();

		if(comps == "E1" || MainGrid.ca_EXPCD.Value == "N"){
			var retVal;
			retVal = dialog("PSSALLIB::SLTZ100D.xml", "", 300,300, "Title=true CloseFlag=false");
			ds_SLTZ100_BOX.LoadXML(retVal);
		}else if(MainGrid.ca_EXPCD.Value == "Y"){
			var z=0;
			if(length(trim(MainGrid.ed_JPSNO1.Text)) > 0){
				ds_SLTZ100_BOX.InsertRow(z);
				ds_SLTZ100_BOX.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO1.Text);
				ds_SLTZ100_BOX.SetColumn(z,"BOXQYB","0");
				ds_SLTZ100_BOX.SetColumn(z,"BOXAMB","0");
				z=z+1;
			}
			if(length(trim(MainGrid.ed_JPSNO2.Text)) > 0){
				ds_SLTZ100_BOX.InsertRow(z);
				ds_SLTZ100_BOX.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO2.Text);
				ds_SLTZ100_BOX.SetColumn(z,"BOXQYB","0");
				ds_SLTZ100_BOX.SetColumn(z,"BOXAMB","0");
				z=z+1;
			}
			if(length(trim(MainGrid.ed_JPSNO3.Text)) > 0){
				ds_SLTZ100_BOX.InsertRow(z);
				ds_SLTZ100_BOX.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO3.Text);
				ds_SLTZ100_BOX.SetColumn(z,"BOXQYB","0");
				ds_SLTZ100_BOX.SetColumn(z,"BOXAMB","0");
				z=z+1;
			}
			if(length(trim(MainGrid.ed_JPSNO4.Text)) > 0){
				ds_SLTZ100_BOX.InsertRow(z);
				ds_SLTZ100_BOX.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO4.Text);
				ds_SLTZ100_BOX.SetColumn(z,"BOXQYB","0");
				ds_SLTZ100_BOX.SetColumn(z,"BOXAMB","0");
				z=z+1;
			}
			if(length(trim(MainGrid.ed_JPSNO5.Text)) > 0){
				ds_SLTZ100_BOX.InsertRow(z);
				ds_SLTZ100_BOX.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO5.Text);
				ds_SLTZ100_BOX.SetColumn(z,"BOXQYB","0");
				ds_SLTZ100_BOX.SetColumn(z,"BOXAMB","0");
				z=z+1;
			}
			if(length(trim(MainGrid.ed_JPSNO6.Text)) > 0){
				ds_SLTZ100_BOX.InsertRow(z);
				ds_SLTZ100_BOX.SetColumn(z,"JPSNOB",MainGrid.ed_JPSNO6.Text);
				ds_SLTZ100_BOX.SetColumn(z,"BOXQYB","0");
				ds_SLTZ100_BOX.SetColumn(z,"BOXAMB","0");
				z=z+1;
			}
		}
	}
	//trace(ds_SLTZ100_BOX.SaveXML());
 	http.Sync = true;
	var sKind		= "SAVE_SLTZ100";						
	var sMethodName = "service::pssallib/insertSLTZ100.do";
	var sInDataSet  = "ds_SLTZ100=ds_SLTZ100:U";
	var sOutDataSet = "ds_SLTZ100_CHECK=ds_SLTZ100_CHECK ds_SLTZ100_LNTNO=ds_SLTZ100_LNTNO";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
 }

/**********************************************************************
 * 내용설명 : 전표번호 저장
 **********************************************************************/
function setSLTZ100_110(){
	var insert_LNTNO = ds_SLTZ100_LNTNO.GetColumn(0,"LNTNO");
	var save_LNTNO = iif(length(insert_LNTNO) > 0,insert_LNTNO,ed_LNTNO.Text)  ;
	//alert(insert_LNTNO);
	var qtyArray = array();
	var jpsnoArray = array();
	for (var i=0; i<ds_SLTZ100_BOX.GetRowCount(); i++){
		jpsnoArray[i] = ds_SLTZ100_BOX.GetColumn(i,"JPSNOB");
		qtyArray[i] = ds_SLTZ100_BOX.GetColumn(i,"BOXQYB");
	}
	http.Sync = true;
	var sKind		= "SAVE_SLTZ110";						
	var sMethodName = "service::pssallib/insertSLTZ100_110.do";
	var sInDataSet  = "";
	var sOutDataSet = "";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " LNTNO=" + quote(save_LNTNO);
	sArgument += " INOUT=" + quote(ca_INOUT.Value);
	sArgument += " EXPCD=" + quote(MainGrid.ca_EXPCD.Value);
	sArgument += " JPSNO1=" + quote(jpsnoArray[0]);
	sArgument += " JPSNO2=" + quote(jpsnoArray[1]);
	sArgument += " JPSNO3=" + quote(jpsnoArray[2]);
	sArgument += " JPSNO4=" + quote(jpsnoArray[3]);
	sArgument += " JPSNO5=" + quote(jpsnoArray[4]);
	sArgument += " JPSNO6=" + quote(jpsnoArray[5]);
	sArgument += " BOXQY1=" + quote(qtyArray[0]);
	sArgument += " BOXQY2=" + quote(qtyArray[1]);
	sArgument += " BOXQY3=" + quote(qtyArray[2]);
	sArgument += " BOXQY4=" + quote(qtyArray[3]);
	sArgument += " BOXQY5=" + quote(qtyArray[4]);
	sArgument += " BOXQY6=" + quote(qtyArray[5]);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
}

/**********************************************************************
 * 내용설명 : mst040pf 최종 발행번호 저장
 **********************************************************************/
 function setMst040pf()
 {
	var insert_LNTNO = ds_SLTZ100_LNTNO.GetColumn(0,"LNTNO");
	if(length(trim(insert_LNTNO)) > 0){  //insert시만 저장
		var sKind		= "setmst040";
		var sMethodName = "service::pscomlib/updateMST040.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "";    		 								// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
		sArgument += " MAJCD=" + quote("LNTNO");
		sArgument += " MJINT=" + quote(insert_LNTNO);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		getBeforLNTNO();
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid| ";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "getSLTZ100"){
		alert(ds_SLTZ100_A.GetRowCount());
	}
	if (svcId = "SAVE_SLTZ100"){
		if (ds_SLTZ100_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_SLTZ100_CHECK.GetRowCount();i++){		
				alert(ds_SLTZ100_CHECK.GetColumn(i,"LNTNO") + " ="
					+ ds_SLTZ100_CHECK.GetColumn(i,"RTMSG"));	
			}
			return;		
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		setMst040pf();   //최종발행번호 저장
		setSLTZ100_110();       //전표번호 저장
		ed_LNTNO.Text = ds_SLTZ100_LNTNO.GetColumn(0,"LNTNO");
		common_OnChanged();
		alert(setPopMessage("1001"));
		setMessage("1001");	
		MainGrid.Enable = false;
	}
}

