function fn_Search()
{
	ed_Action.Text = "Q";
	getSLTZ100();
	MainGrid.Enable = true;
}

function fn_Input()
{
	ed_Action.Text = "A";
	if(length(trim(ed_LNTNO.Text)) > 0){
		ed_LNTNO.Text ="";  //추가후 저장시  발주번호 자동생성
	}
	if(checkInsertCount("ds_SLTZ100")== false) return;
	ds_SLTZ100.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	ds_SLTZ100.AddRow();
	//getBeforLNTNO();
	ds_SLTZ100.SetColumn(ds_SLTZ100.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
	ds_SLTZ100.SetColumn(ds_SLTZ100.Row, "SHPTO", "0"); // default값 설정
	ds_SLTZ100.SetColumn(ds_SLTZ100.Row, "INOUT", ca_INOUT.Value); // default값 설정
	ds_SLTZ100.FireEvent=true;
	if(comps != "E1"){
		MainGrid.ca_EXPCD.Index = 0 ;
	}else{
		MainGrid.ca_EXPCD.Index = 1 ;
		MainGrid.med_USEAM.Visible = false;
		MainGrid.Static44.Visible = false;
		MainGrid.Static45.Visible = false;
	}
	MainGrid.ca_USEDT.value = nowDate;
	MainGrid.ca_ARVDT.value = nowDate;
	MainGrid.ca_EXPCD.SetFocus();

	MainGrid.ed_JPSNO1 = "";
	MainGrid.ed_JPSNO2 = "";
	MainGrid.ed_JPSNO3 = "";
	MainGrid.ed_JPSNO4 = "";
	MainGrid.ed_JPSNO5 = "";
	MainGrid.ed_JPSNO6 = "";

	MainGrid.Enable = true;
}

function fn_Delete()
{
	if(length(trim(ed_LNTNO.Text)) < 1){
		alert(setPopMessage("4004"));  //
		setMessage("4004");	
		ed_LNTNO.SetFocus();
		return;
	}
	if(ds_SLTZ100.rowcount < 1) getSLTZ100();
	if (MainGrid.MaskEdit20.Text == "*"){
		alert(setPopMessage("4005"));  //  [확인] 용차사용신청확인 마감작업이 완료되어 수정 불가능합니다.
		setMessage("4005");
		return;
	}
	ed_Action.Text = "Q";
	MainGrid.ed_JPSNO1 = "";
	MainGrid.ed_JPSNO2 = "";
	MainGrid.ed_JPSNO3 = "";
	MainGrid.ed_JPSNO4 = "";
	MainGrid.ed_JPSNO5 = "";
	MainGrid.ed_JPSNO6 = "";
	ds_SLTZ100_BOX.ClearData();
	ds_SLTZ100.DeleteRow(ds_SLTZ100.currow);

	alert(setPopMessage("1011"));     //[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.
	setMessage("1011");	
}

