function ds_SLTZ100_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;  //transaction호출후 저장이벤트가 발생되지 않아서...
	if (strColumnID == "PYVND"){
		//득의선Check
		http.Sync 	= true;
		var sKind		= "getOGMPrtno";		
		var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " CUSNO=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		if (ds_CUSMAS1.GetRowCount() < 1){
			alert(setPopMessage("4007"));
			setMessage("4007");	
			MainGrid.ed_pyvnd.SetFocus();
			return false;
		}
		PYVND_NM=ds_CUSMAS1.GetColumn(0,"CUSNM");
	}
	if (strColumnID == "MKLOC"){
		http.Sync = true;
		var sKind		= "getmst050pf";		
		var sMethodName = "service::pscomlib/selectMST050PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
			sArgument  += " GRPCD=" + quote("LOC");
			sArgument  += " MSTCD=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST050PF.GetRowCount() < 1){
			alert(setPopMessage("4008"));
			setMessage("4008");	
			return false;
		}
		MKLOC_NM=ds_MST050PF.GetColumn(0,"MSTRM");
	}
	if (strColumnID == "CARSQ"){
		if(length(trim(varNewValue)) > 0){
			http.Sync = true;
			var sKind		= "getmst050pf";		
			var sMethodName = "service::pscomlib/selectMST050PF.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_MST050PF=ds_MST050PF";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
				sArgument  += " GRPCD=" + quote("SLC");
				sArgument  += " MSTCD=" + quote(varNewValue);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_MST050PF.GetRowCount() < 1){
				alert(setPopMessage("4009"));
				setMessage("4009");	
				return false;
			}
			CARSQ_NM=ds_MST050PF.GetColumn(0,"MSTRM");
		}
	}
	if (strColumnID == "USEGB"){
		//운행구분check
		http.Sync = true;
		var sKind		= "getmst050pf";		
		var sMethodName = "service::pscomlib/selectMST050PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
			sArgument  += " GRPCD=" + quote("USE");
			sArgument  += " MSTCD=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST050PF.GetRowCount() < 1){
			alert(setPopMessage("4011"));
			setMessage("4011");	
			return false;
		}
		USEGB_NM=ds_MST050PF.GetColumn(0,"MSTRM");
	}
	if (strColumnID == "TRNCD"){
		//경유지check
		http.Sync = true;
		var sKind		= "getmst050pf";		
		var sMethodName = "service::pscomlib/selectMST050PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
			sArgument  += " GRPCD=" + quote("TRN");
			sArgument  += " MSTCD=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST050PF.GetRowCount() < 1){
			alert(setPopMessage("4014"));
			setMessage("4014");	
			return false;
		}
		TRNCD_NM=ds_MST050PF.GetColumn(0,"MSTRM");
	}
	if (strColumnID == "DRVPN"){
		//운수업체check
		http.Sync = true;
		var sKind		= "getmst600pf";		
		var sMethodName = "service::pscomlib/selectMST600PF.do"; 
		var sInDataSet  = "";
