	}
	http.usewaitcursor = true;
}

function ds_SLTZ100_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "PYVND"){
		obj.SetColumn(nRow, "CUSNM", PYVND_NM); // 납품업체명 설정
	}
	if (strColumnID == "MKLOC"){
		obj.SetColumn(nRow, "MSTRM1", MKLOC_NM); // 납품장소명 설정
	}
	if (strColumnID == "CARSQ"){
		obj.SetColumn(nRow, "MSTRM4", CARSQ_NM); // 납품장소명 설정
	}
	if (strColumnID == "USEGB"){
		obj.SetColumn(nRow, "MSTRM2", USEGB_NM); // 사용구분명명 설정
	}
	if (strColumnID == "TRNCD"){
		obj.SetColumn(nRow, "MSTSR3", TRNCD_NM); // 경유지명 설정
	}
	if (strColumnID == "DRVPN"){
		obj.SetColumn(nRow, "CAPNM", DRVPN_NM); // 운송거래처명 설정
	}
	http.usewaitcursor = true;
}

function MainGrid_common_OnChanged(obj,strText)
{
	change_JPSNO = "T";
	var len = length(trim(strText));
	if (len != 0){
		obj.text = iif(len < 7, right("0000000"+trim(strText),7),trim(strText));
	}
}
function MainGrid_med_ARVTM_OnChanged(obj,strText)
{
	var strChk = replace(strText,':','');

	if((left(strChk,2) > 24) || (right(strChk,2) > 59)){
		alert("[확인] 시간형식 오류");
		obj.value = "";
	}
}

function MainGrid_ca_EXPCD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strText == "N"){
		MainGrid.med_USEAM.Visible = false;
		MainGrid.Static44.Visible = false;
		MainGrid.Static45.Visible = false;
	}else{
		MainGrid.med_USEAM.Visible = true;
		MainGrid.Static44.Visible = true;
		MainGrid.Static45.Visible = true;
	}
}

function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");
		}
	}
}
function MainGrid_med_DRVPN_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}
